global.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
    /* Typography tokens */
    --font-brand: "Golos Text";
    --font-sans: var(--font-brand), ui-sans-serif, system-ui, sans-serif;

    /* Color tokens (RGB triples for Tailwind opacity support) */
    --color-ink: 11 12 16;
    --color-mist: 247 244 239;
    --color-clay: 215 197 176;
    --color-tide: 18 93 93;
    --color-primary: 11 13 12;
    --color-secondary: 75 180 98;
    --color-cursor-trail: var(--color-secondary);
    --color-page-bg: var(--color-mist);

    color-scheme: dark light;
}

html {
    scroll-behavior: smooth;
    scrollbar-width: none;
    -ms-overflow-style: none;
}

body {
    font-family: var(--font-sans);
    background-color: rgb(var(--color-page-bg));
    scrollbar-width: none;
    -ms-overflow-style: none;
}

html::-webkit-scrollbar,
body::-webkit-scrollbar {
    width: 0;
    height: 0;
    display: none;
}

::selection {
    background: rgb(var(--color-tide));
    color: white;
}

styleBlocks.css:
.about-cta-section {
  margin-top: 80px;
}

.about-cta-card {
  position: relative;
  overflow: hidden;
  min-height: 422px;
  border-radius: 52px;
  background: linear-gradient(241.25deg, rgba(255, 255, 255, 0.1) 0.52%, rgba(170, 157, 145, 0.1) 101.5%);
  box-shadow:
    inset 0 0 0 1px rgba(255, 255, 255, 0.05),
    0 18px 42px rgba(0, 0, 0, 0.28);
  isolation: isolate;
}

.about-cta-card::before {
  content: "";
  position: absolute;
  inset: 0;
  z-index: 0;
  pointer-events: none;
  background:
    radial-gradient(52% 120% at 79% 50%, rgba(247, 144, 9, 0.19) 0%, rgba(247, 144, 9, 0) 72%),
    linear-gradient(92deg, rgba(13, 14, 18, 0.4) 0%, rgba(13, 14, 18, 0.02) 52%, rgba(13, 14, 18, 0.34) 100%);
}

.about-cta-copy {
  position: relative;
  z-index: 2;
  width: 510px;
  max-width: calc(100% - 440px);
  padding: 69px 0 69px 80px;
  color: #fff;
}

.about-cta-title {
  margin: 0;
  display: flex;
  flex-direction: column;
  font-family: "Golos Text", sans-serif;
  font-size: clamp(30px, 2.9vw, 36px);
  line-height: 1.5;
  font-weight: 600;
  letter-spacing: 0;
}

.about-cta-title span {
  display: block;
  white-space: nowrap;
}

.about-cta-title-accent {
  color: #f79009;
}

.about-cta-description {
  margin: 24px 0 0;
  font-family: "Golos Text", sans-serif;
  font-size: clamp(18px, 1.8vw, 20px);
  line-height: 1.8;
  font-weight: 500;
  letter-spacing: 0.015em;
  color: rgba(255, 255, 255, 0.95);
}

.about-cta-actions {
  margin-top: 36px;
  display: flex;
  flex-wrap: nowrap;
  align-items: center;
  gap: 12px;
}

.about-cta-action {
  display: inline-flex;
  height: 44px;
  align-items: center;
  justify-content: center;
  gap: 8px;
  border-radius: 32px;
  background: rgba(255, 255, 255, 0.1);
  padding: 10px 24px 10px 16px;
  color: #fff;
  text-decoration: none;
  font-family: "Golos Text", sans-serif;
  font-size: 14px;
  line-height: 18px;
  font-weight: 500;
  white-space: nowrap;
  border: 0;
  cursor: pointer;
  transition:
    background-color 200ms ease,
    transform 200ms ease;
}

.about-cta-action img {
  width: 24px;
  height: 24px;
  flex-shrink: 0;
}

.about-cta-action:hover {
  background: rgba(255, 255, 255, 0.18);
  transform: translateY(-1px);
}

.about-cta-action:focus-visible {
  background: rgba(255, 255, 255, 0.18);
  transform: translateY(-1px);
  outline: 2px solid rgb(var(--color-secondary));
  outline-offset: 2px;
}

.about-cta-visual {
  position: absolute;
  right: 70px;
  top: 28px;
  z-index: 1;
  width: 292px;
  height: 356px;
  pointer-events: none;
}

.about-cta-visual-glow {
  position: absolute;
  left: 50%;
  top: 52%;
  width: 234px;
  height: 234px;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  background:
    radial-gradient(circle, rgba(248, 208, 124, 0.28) 0%, rgba(248, 208, 124, 0.14) 46%, rgba(248, 208, 124, 0) 78%);
  filter: blur(8px);
}

.about-cta-illustration {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  object-fit: contain;
  object-position: center;
  filter: drop-shadow(0 14px 36px rgba(0, 0, 0, 0.4));
}

@media (max-width: 1320px) {
  .about-cta-card {
    border-radius: 42px;
  }

  .about-cta-copy {
    width: min(100%, 500px);
    max-width: calc(100% - 332px);
    padding-left: 44px;
  }

  .about-cta-visual {
    right: 20px;
    top: 34px;
    width: 272px;
    height: 332px;
  }
}

@media (max-width: 1080px) {
  .about-cta-card {
    min-height: 0;
    border-radius: 36px;
    padding: 34px 28px;
    display: grid;
    gap: 28px;
    justify-items: center;
  }

  .about-cta-copy {
    width: 100%;
    max-width: 720px;
    padding: 0;
  }

  .about-cta-title span {
    white-space: normal;
  }

  .about-cta-title {
    font-size: clamp(30px, 5.6vw, 36px);
  }

  .about-cta-description {
    font-size: clamp(17px, 2.5vw, 20px);
  }

  .about-cta-actions {
    margin-top: 24px;
    flex-wrap: wrap;
  }

  .about-cta-visual {
    position: relative;
    top: auto;
    right: auto;
    width: min(100%, 300px);
    height: auto;
    aspect-ratio: 317 / 386;
    justify-self: center;
  }
}

@media (min-width: 744px) and (max-width: 1366px) and (any-pointer: coarse) {
  .about-cta-card {
    min-height: 786px;
    border-radius: 52px;
    padding: 44px 0;
    display: grid;
    grid-template-rows: auto 1fr auto;
    align-items: start;
  }

  .about-cta-copy {
    width: 510px;
    max-width: calc(100% - 48px);
    padding: 0;
    margin: 0 auto;
    text-align: center;
  }

  .about-cta-title {
    font-size: 36px;
    line-height: 1.5;
  }

  .about-cta-title span {
    white-space: nowrap;
  }

  .about-cta-description {
    margin-top: 24px;
    font-size: 20px;
    line-height: 1.8;
    letter-spacing: 0.015em;
  }

  .about-cta-actions {
    position: relative;
    left: auto;
    bottom: auto;
    margin: 12px auto 0;
    transform: none;
    flex-wrap: nowrap;
    justify-content: center;
    gap: 12px;
    z-index: 2;
  }

  .about-cta-visual {
    position: relative;
    top: auto;
    left: auto;
    right: auto;
    width: 317px;
    height: 386px;
    margin: 20px auto 0;
    transform: none;
  }
}

@media (max-width: 743px) {
  .about-cta-card {
    min-height: 558px;
    border-radius: 32px;
    padding: 0;
    display: block;
  }

  .about-cta-copy {
    width: min(332px, calc(100% - 40px));
    max-width: none;
    padding: 32px 0 0;
    margin: 0 auto;
    position: static;
    text-align: center;
  }

  .about-cta-title {
    font-size: 20px;
    line-height: 24px;
    font-weight: 600;
  }

  .about-cta-title span {
    white-space: normal;
  }

  .about-cta-description {
    margin-top: 16px;
    font-size: 14px;
    line-height: 22px;
    letter-spacing: 0;
  }

  .about-cta-actions {
    position: absolute;
    left: 20px;
    right: 20px;
    bottom: 20px;
    margin-top: 0;
    margin-bottom: 0;
    display: grid;
    grid-template-columns: repeat(2, minmax(0, 1fr));
    gap: 8px;
    z-index: 2;
  }

  .about-cta-action {
    width: 100%;
    justify-content: center;
    padding: 10px 12px;
  }

  .about-cta-action:nth-child(3) {
    grid-column: 1 / -1;
  }

  .about-cta-visual {
    position: absolute;
    left: 50%;
    right: auto;
    top: 206px;
    width: 150px;
    height: 225px;
    margin: 0;
    transform: translateX(-50%);
  }

  .about-cta-visual-glow {
    width: 180px;
    height: 180px;
    top: 55%;
  }
}

#cursor-trail-canvas {
  position: fixed;
  inset: 0;
  pointer-events: none;
  z-index: 120;
}

@media (pointer: coarse) {
  #cursor-trail-canvas {
    display: none;
  }
}

.hero-actions button {
  border: 0;
  cursor: pointer;
  font-family: inherit;
}

.video-modal {
  position: fixed;
  inset: 0;
  z-index: 1600;
  display: grid;
  place-items: center;
  padding: 24px;
}

.video-modal[hidden] {
  display: none;
}

.video-modal__backdrop {
  position: absolute;
  inset: 0;
  background: rgba(6, 8, 12, 0.78);
  backdrop-filter: blur(4px);
}

.video-modal__dialog {
  position: relative;
  z-index: 1;
  width: min(960px, calc(100vw - 32px));
  border-radius: 20px;
  overflow: hidden;
  background: #0d1018;
  border: 1px solid rgba(255, 255, 255, 0.18);
  box-shadow: 0 30px 80px rgba(0, 0, 0, 0.52);
}

.video-modal__player {
  width: 100%;
  height: auto;
  max-height: 80vh;
  display: block;
  background: #000;
}

.video-modal__close {
  position: absolute;
  top: 10px;
  right: 10px;
  z-index: 2;
  width: 38px;
  height: 38px;
  border: 0;
  border-radius: 999px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  color: #fff;
  background: rgba(0, 0, 0, 0.5);
  cursor: pointer;
  transition: background-color 160ms ease;
}

.video-modal__close:hover {
  background: rgba(0, 0, 0, 0.72);
}

.video-modal__close:focus-visible {
  background: rgba(0, 0, 0, 0.72);
  outline: 2px solid rgb(var(--color-secondary));
  outline-offset: 2px;
}

.video-modal__close svg {
  width: 20px;
  height: 20px;
  fill: none;
  stroke: currentColor;
}

:global(body.video-modal-open) {
  overflow: hidden;
}

