.how-work-root {
  width: 100%;
  margin: 0 auto;
}

.how-work-section {
  position: relative;
  width: 100vw;
  min-height: 100svh;
  height: 100svh;
  margin-left: calc(50% - 50vw);
  margin-right: calc(50% - 50vw);
  margin-top: 60px;
  margin-bottom: 60px;
  overflow-x: clip;
  z-index: 1;
}

.how-work-card {
  position: relative;
  width: 100%;
  height: 100svh;
  overflow: hidden;
  border-radius: 0;
  border: 0;
  background: linear-gradient(74.45deg, #142919 21.82%, #222422 41.45%, #1a3320 103.51%);
  isolation: isolate;
}

.how-work-grid {
  position: absolute;
  inset: 0;
  background-image:
    repeating-linear-gradient(0deg, rgba(255, 255, 255, 0.16) 0 1px, transparent 1px 56px),
    repeating-linear-gradient(90deg, rgba(255, 255, 255, 0.16) 0 1px, transparent 1px 56px);
  background-size: 112px 112px;
  mix-blend-mode: hard-light;
  opacity: 0.1;
  transform: matrix(-1, 0, 0, 1, 0, 0);
  pointer-events: none;
  z-index: 0;
}

.how-work-title {
  position: absolute;
  left: 50%;
  top: clamp(24px, 4.8svh, 42px);
  transform: translateX(-50%);
  margin: 0;
  text-align: center;
  color: #fff;
  font-family: "Golos Text", sans-serif;
  font-size: clamp(34px, 4vw, 48px);
  line-height: 1;
  font-weight: 600;
  z-index: 2;
  white-space: nowrap;
}

.how-work-title-accent {
  color: #fff;
}

.how-work-step-wrap {
  position: absolute;
  right: clamp(26px, 3.4vw, 44px);
  top: clamp(88px, 11svh, 130px);
  z-index: 2;
  pointer-events: none;
  overflow: hidden;
}

.how-work-layout {
  position: relative;
  z-index: 2;
  display: grid;
  grid-template-columns: 460px minmax(210px, 1fr) 420px;
  align-items: center;
  gap: 42px;
  height: 100%;
  padding: clamp(132px, 16svh, 148px) 35px clamp(20px, 4svh, 40px);
}

.how-work-left {
  width: 100%;
  position: relative;
  min-height: clamp(330px, 44svh, 430px);
  transition: height 780ms cubic-bezier(0.77, 0, 0.175, 1);
}

.how-work-panel {
  position: absolute;
  inset: 0;
  display: flex;
  flex-direction: column;
  gap: 16px;
  width: 100%;
  max-width: 100%;
  opacity: 0;
  transform: translate3d(0, 100%, 0);
  filter: blur(4px);
  pointer-events: none;
  box-sizing: border-box;
}

.how-work-panel.is-active {
  opacity: 1;
  transform: translate3d(0, 0, 0);
  filter: blur(0);
  pointer-events: auto;
}

.how-work-item {
  border-radius: 28px;
  border-left: 5px solid #4bb462;
  background: rgba(255, 255, 255, 0.1);
  padding: 22px 24px 22px 28px;
  backdrop-filter: blur(4px);
  width: 100%;
  max-width: 100%;
  box-sizing: border-box;
  overflow: visible;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  gap: 8px;
}

.how-work-item.is-warning {
  border-left-color: #f79009;
}

.how-work-item-label {
  margin: 0;
  width: 100%;
  font-family: "Golos Text", sans-serif;
  font-size: 20px;
  line-height: 26px;
  font-weight: 700;
  color: #4bb462;
}

.how-work-item.is-warning .how-work-item-label {
  color: #f79009;
}

.how-work-item-text {
  margin: 0;
  width: 100%;
  max-width: 100%;
  font-family: "Golos Text", sans-serif;
  font-size: 17px;
  line-height: 168%;
  font-weight: 400;
  color: rgba(255, 255, 255, 0.82);
  overflow-wrap: anywhere;
  word-break: break-word;
}

.how-work-phone {
  --mockup-scale: 1.04;
  --screen-scale: 1;
  position: relative;
  justify-self: center;
  width: clamp(210px, 19vw, 242px);
  aspect-ratio: 574 / 1180;
}

.how-work-screen {
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  border-radius: 16px;
  background: #0b0c10;
  z-index: 1;
  transform: scale(var(--screen-scale));
  transform-origin: center;
}

/* GIF clips strictly inside screen bounds */
.how-work-gif {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: top center;
  opacity: 0;
  transform: translate3d(0, 100%, 0);
  filter: blur(4px);
}

.how-work-gif.is-active {
  opacity: 1;
  transform: translate3d(0, 0, 0);
  filter: blur(0);
}

.how-work-mockup {
  position: relative;
  z-index: 2;
  display: block;
  width: 100%;
  height: 100%;
  object-fit: contain;
  transform: scale(var(--mockup-scale));
  transform-origin: center;
  pointer-events: none;
}

.how-work-right {
  justify-self: end;
  width: 100%;
  max-width: 420px;
  text-align: right;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  gap: 16px;
}

.how-work-step {
  margin: 0;
  color: #4bb462;
  font-family: "Golos Text", sans-serif;
  font-size: clamp(76px, 8vw, 120px);
  line-height: 0.9;
  letter-spacing: 0.02em;
  font-weight: 700;
  text-shadow: 0 10px 26px rgba(75, 180, 98, 0.24);
}

.how-work-tab-list {
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  gap: 18px;
}

.how-work-tab {
  border: 0;
  background: transparent;
  color: #85888e;
  text-align: right;
  font-family: "Golos Text", sans-serif;
  font-size: 24px;
  line-height: 1.15;
  font-weight: 500;
  padding: 0;
  cursor: pointer;
  transition: color 240ms ease, font-size 220ms ease;
  white-space: nowrap;
}

.how-work-tab:hover {
  color: #d6d8de;
}

.how-work-tab:focus-visible {
  color: #d6d8de;
  outline: 2px solid rgb(var(--color-secondary));
  outline-offset: 4px;
  border-radius: 4px;
}

.how-work-tab[data-active="true"] {
  color: #4bb462;
  font-size: 32px;
  line-height: 1.1;
  font-weight: 700;
}

