  background: transparent;
}

[data-site-header][data-menu-open="true"] {
  z-index: 90;
}

[data-site-header].is-stuck {
  position: sticky;
  top: 0;
  border-bottom-color: rgb(255 255 255 / 0.1);
  background: rgb(11 12 16 / 0.85);
  backdrop-filter: blur(10px);
}

.site-header-bar {
  position: relative;
  z-index: 95;
}

.site-header-menu {
  position: relative;
  width: 24px;
  height: 24px;
  display: none;
  align-items: center;
  justify-content: center;
  color: rgb(255 255 255 / 0.95);
  border: 0;
  padding: 0;
  background: transparent;
  cursor: pointer;
}

.site-header-menu-glyph {
  position: absolute;
  inset: 0;
  width: 24px;
  height: 24px;
  fill: none;
  stroke: currentColor;
  transform-origin: center;
  transition:
    opacity 180ms ease,
    transform 200ms ease;
}

.site-header-menu-glyph--close {
  opacity: 0;
  transform: scale(0.75) rotate(-8deg);
}

[data-site-header][data-menu-open="true"] .site-header-menu-glyph--burger {
  opacity: 0;
  transform: scale(0.75) rotate(8deg);
}

[data-site-header][data-menu-open="true"] .site-header-menu-glyph--close {
  opacity: 1;
  transform: scale(1) rotate(0deg);
}

.site-header-panel {
  display: none;
  position: fixed;
  top: var(--site-header-h, 72px);
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 60;
  background: #121212;
  opacity: 0;
  transform: translateY(-10px);
  pointer-events: none;
  transition:
    opacity 240ms ease,
    transform 260ms ease;
  overflow-y: auto;
  overscroll-behavior: contain;
}

.site-header-panel::before,
.site-header-panel::after {
  content: "";
  position: absolute;
  width: 62vw;
  height: 62vw;
  min-width: 360px;
  min-height: 360px;
  border-radius: 999px;
  filter: blur(160px);
  opacity: 0.28;
  pointer-events: none;
}

.site-header-panel::before {
  left: max(-22vw, -280px);
  top: max(-14vw, -220px);
  background: #e2761c;
}

.site-header-panel::after {
  right: max(-20vw, -260px);
  top: clamp(120px, 30vh, 320px);
  background: #239fc1;
}

.site-header-panel-inner {
  position: relative;
  z-index: 1;
  width: min(704px, calc(100vw - 40px));
  min-height: calc(100dvh - var(--site-header-h, 72px));
  margin: 0 auto;
  padding: 16px 0 22px;
  display: flex;
  flex-direction: column;
}

.site-header-panel-nav {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.site-header-link {
  color: rgb(255 255 255 / 0.95);
  text-decoration: none;
}

.site-header-link:hover,
.site-header-link.is-active {
  color: #4bb462;
}

.site-header-link:focus-visible {
  color: #4bb462;
  outline: 2px solid rgb(var(--color-secondary));
  outline-offset: 2px;
  border-radius: 4px;
}

.site-header-panel-link {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 52px;
  border-radius: 32px;
  padding: 10px;
  color: #fff;
  text-decoration: none;
  font-family: "Golos Text", sans-serif;
  font-size: 16px;
  line-height: 20px;
  font-weight: 500;
  background:
    linear-gradient(90deg, rgba(226, 118, 28, 0.14) 0%, rgba(35, 159, 193, 0.18) 100%),
    rgba(255, 255, 255, 0.1);
  transition:
    background-color 160ms ease,
    color 160ms ease,
    transform 180ms ease;
}

.site-header-panel-link:hover,
.site-header-panel-link.is-active {
  background:
    linear-gradient(90deg, rgba(226, 118, 28, 0.22) 0%, rgba(35, 159, 193, 0.26) 100%),
    rgba(255, 255, 255, 0.16);
  color: #4bb462;
  transform: translateY(-1px);
}

.site-header-panel-link:focus-visible {
  background:
    linear-gradient(90deg, rgba(226, 118, 28, 0.22) 0%, rgba(35, 159, 193, 0.26) 100%),
    rgba(255, 255, 255, 0.16);
  color: #4bb462;
  transform: translateY(-1px);
  outline: 2px solid rgb(var(--color-secondary));
  outline-offset: 2px;
}

.site-header-panel-contact {
  margin-top: clamp(52px, 11vh, 168px);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
}

.site-header-panel-contact-title {
  margin: 0;
  color: #fff;
  font-family: "Golos Text", sans-serif;
  font-size: 16px;
  line-height: 20px;
  font-weight: 500;
  text-align: center;
}

.site-header-panel-phone {
  margin: 0;
  color: #dcdddf;
  text-decoration: none;
  font-family: "Golos Text", sans-serif;
  font-size: clamp(30px, 3.4vw, 40px);
  line-height: 1.15;
  font-weight: 500;
  text-align: center;
  transition: color 160ms ease;
}

.site-header-panel-phone:hover {
  color: #4bb462;
}

.site-header-panel-phone:focus-visible {
  color: #4bb462;
  outline: 2px solid rgb(var(--color-secondary));
  outline-offset: 2px;
  border-radius: 4px;
}

.site-header-panel-socials {
  display: flex;
  align-items: center;
  gap: 12px;
}

.site-header-panel-social-link {
  width: 56px;
  height: 44px;
  border-radius: 32px;
  background: rgba(255, 255, 255, 0.1);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  transition:
    background-color 160ms ease,
    transform 180ms ease;
}

.site-header-panel-social-link img {
  width: 24px;
  height: 24px;
}

.site-header-panel-social-link:hover {
  background: rgba(255, 255, 255, 0.18);
  transform: translateY(-1px);
}

.site-header-panel-social-link:focus-visible {
  background: rgba(255, 255, 255, 0.18);
  transform: translateY(-1px);
  outline: 2px solid rgb(var(--color-secondary));
  outline-offset: 2px;
}

.site-header-panel-store-row {
  width: min(362px, 100%);
  display: grid;
  grid-template-columns: repeat(2, minmax(0, 1fr));
  gap: 8px;
}

.site-header-panel-store-link {
  min-height: 44px;
  border-radius: 32px;
  background: rgba(255, 255, 255, 0.1);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
  padding: 10px 16px 10px 14px;
  color: #fff;
  text-decoration: none;
  font-family: "Golos Text", sans-serif;
  font-size: 14px;
  line-height: 18px;
  font-weight: 500;
  transition:
    background-color 160ms ease,
    transform 180ms ease;
}

.site-header-panel-store-link img {
  width: 24px;
  height: 24px;
}

.site-header-panel-store-link:hover {
  background: rgba(255, 255, 255, 0.18);
  transform: translateY(-1px);
}

.site-header-panel-store-link:focus-visible {
  background: rgba(255, 255, 255, 0.18);
  transform: translateY(-1px);
  outline: 2px solid rgb(var(--color-secondary));
  outline-offset: 2px;
}

.site-header-panel-bottom {
  margin-top: auto;
  min-height: 64px;
  border-top: 1px solid rgba(255, 255, 255, 0.14);
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
}

.site-header-panel-bottom p {
  margin: 0;
  color: #dcdddf;
  font-family: "Golos Text", sans-serif;
  font-size: 12px;
  line-height: 14px;
  font-weight: 400;
}

@media (max-width: 743px), (min-width: 744px) and (max-width: 1366px) and (any-pointer: coarse) {
  .site-header-desktop-nav,
  .site-header-desktop-divider {
    display: none !important;
  }

  :global(body.site-menu-open) {
    overflow: hidden;
  }

  .site-header-menu {
    display: inline-flex;
  }

  .site-header-panel {
    display: block;
  }

  [data-site-header][data-menu-open="true"] .site-header-panel {
    opacity: 1;
    transform: translateY(0);
    pointer-events: auto;
  }
}

@media (max-width: 743px) {
  [data-site-header] {
    --site-header-h: 56px;
  }

  [data-site-header].is-stuck {
    position: sticky;
    top: 0;
    border-bottom-color: rgb(255 255 255 / 0.1);
    background: rgb(11 12 16 / 0.92);
    backdrop-filter: blur(8px);
  }

  [data-site-header] > .site-header-bar {
    height: 56px;
  }

  [data-site-header] a img {
    height: 25px;
  }

  [data-site-header] details > summary {
    font-size: 16px;
    line-height: 20px;
    gap: 4px;
  }

  .site-header-panel-inner {
    width: min(362px, calc(100vw - 40px));
    padding-top: 12px;
    padding-bottom: 16px;
  }

  .site-header-panel-link {
    min-height: 50px;
    padding: 9px 10px;
    font-size: 15px;
    line-height: 19px;
  }

  .site-header-panel-contact {
    margin-top: clamp(40px, 8vh, 92px);
    gap: 12px;
  }

  .site-header-panel-phone {
    font-size: clamp(34px, 9vw, 40px);
  }

  .site-header-panel-social-link {
    width: 52px;
    height: 44px;
  }

  .site-header-panel-store-row {
    width: 100%;
    gap: 8px;
  }
}

/* Full-width section on all viewports */
.problem-section {
  width: 100vw;
  max-width: 100vw;
  margin-left: calc(50% - 50vw);
  margin-right: calc(50% - 50vw);
  overflow-x: clip;
  box-sizing: border-box;
  padding-top: 0;
  padding-bottom: 0;
}

.problem-inner {
  width: 100%;
  max-width: 1360px;
  margin: 0 auto;
  padding: 0 20px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  overflow-x: clip;
}

.problem-section + .problem-section {
  margin-top: 80px;
}

#not-enough.problem-section {
  padding-bottom: 120px;
}

.problem-root {
  --accent-color: #fdb022;
  --title-line-w: 461.37px;
  --title-line-y: -56%;
  --title-line-w-md: 300px;
  --title-line-w-sm: 210px;
  --card-border-color: #b8804b;
  --card-bg-top: linear-gradient(280.32deg, #103037 -14.08%, #2e2317 110.46%);
  --card-bg-bottom: linear-gradient(
    260.34deg,
    rgba(255, 255, 255, 0.1) -0.52%,
    rgba(170, 157, 145, 0.1) 101.5%
  );
  --body-width: 560px;
  --body-left: 80px;
  --body-min-h: var(--body-h);
  --copy-width: 560px;
  --copy-head-gap: 16px;
  --kicker-color: #fff;
  --kicker-weight: 500;
  --kicker-size: 20px;
  --kicker-line-height: 24px;
  --kicker-size-md: 18px;
  --kicker-line-height-md: 22px;
  --kicker-transform: uppercase;
  --value-color: #fdb022;
  --value-size: 48px;
  --value-line-height: 58px;
  --highlight-color: #fdb022;
  --highlight-size: 28px;
  --highlight-line-height: 110%;
  --highlight-weight: 600;
  --description-line-height: 170%;
  --quote-bg: rgba(255, 255, 255, 0.1);
  --quote-border-color: rgba(255, 255, 255, 0.14);
  --quote-text-color: #fff;
  --quote-size: 16px;
  --quote-line-height: 150%;
  --source-chip-width: min(100%, var(--source-w));
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  gap: 32px;
  isolation: isolate;
  width: 100%;
  overflow-x: clip;
}

.problem-root.is-consequences {
  --accent-color: #f04438;
  --title-line-w: 226px;
  --title-line-y: -50%;
  --title-line-w-md: 208px;
  --title-line-w-sm: 160px;
  --card-bg-top: linear-gradient(-58.62deg, #1f170a 14.08%, #352121 110.46%);
  --card-bg-bottom: linear-gradient(
    240.31deg,
    rgba(255, 255, 255, 0.1) 0.52%,
    rgba(170, 157, 145, 0.1) 101.5%
  );
  --body-width: 600px;
  --body-left: 78px;
  --body-min-h: 252px;
  --copy-width: 600px;
  --copy-head-gap: 0;
  --kicker-color: #f04438;
  --kicker-weight: 600;
  --kicker-size: 36px;
  --kicker-line-height: 100%;
  --kicker-size-md: 32px;
  --kicker-line-height-md: 100%;
  --kicker-transform: none;
  --source-chip-width: fit-content;
}

.problem-root.is-world-solution {
  --accent-color: #4bb462;
  --title-line-w: 230px;
  --title-line-y: -48%;
  --title-line-w-md: 205px;
  --title-line-w-sm: 165px;
  --card-border-color: #4bb462;
  --card-bg-top: linear-gradient(280deg, #0f2518 -12%, #173b23 112%);
  --card-bg-bottom: linear-gradient(
    255deg,
    rgba(255, 255, 255, 0.12) -0.52%,
    rgba(88, 120, 95, 0.1) 101.5%
  );
  --body-width: 650px;
  --body-left: 72px;
  --body-min-h: 338px;
  --copy-width: 650px;
  --copy-head-gap: 6px;
  --kicker-color: #4bb462;
  --kicker-weight: 700;
  --kicker-size: 44px;
  --kicker-line-height: 110%;
  --kicker-size-md: 34px;
  --kicker-line-height-md: 110%;
  --kicker-transform: none;
  --highlight-color: #fdb022;
  --highlight-size: 32px;
  --highlight-line-height: 115%;
  --highlight-weight: 600;
  --description-line-height: 160%;
  --quote-bg: rgba(255, 255, 255, 0.08);
  --quote-border-color: rgba(255, 255, 255, 0.2);
  --quote-size: 20px;
  --quote-line-height: 160%;
  --source-chip-width: fit-content;
}

.problem-root.is-not-enough {
  --accent-color: #f79009;
  --title-line-w: 380px;
  --title-line-y: -50%;
  --title-line-w-md: 260px;
  --title-line-w-sm: 185px;
  --card-border-color: #d17254;
  --card-bg-top: linear-gradient(280deg, #2a1b14 -12%, #3b1f1d 112%);
  --card-bg-bottom: linear-gradient(
    255deg,
    rgba(255, 255, 255, 0.12) -0.52%,
    rgba(170, 157, 145, 0.1) 101.5%
  );
  --body-width: 560px;
  --body-left: 72px;
  --body-min-h: 248px;
  --copy-width: 560px;
  --copy-head-gap: 20px;
  --kicker-color: #fdb022;
  --kicker-weight: 700;
  --kicker-size: 36px;
  --kicker-line-height: 100%;
  --kicker-size-md: 34px;
  --kicker-line-height-md: 110%;
  --kicker-transform: none;
  --description-line-height: 24px;
}

.problem-root.is-not-enough .problem-body {
  gap: 32px;
}

.problem-root.is-not-enough .problem-layout {
  --card-h: 400px;
  --container-card-h: 500px;
  --container-extra: 280px;
}

.problem-root.is-not-enough .slider-container {
  overflow: visible;
}

.problem-title-wrap {
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 24px;
  width: 100%;
  min-height: 102px;
}

.problem-title {
  width: 100%;
  margin: 0;
  text-align: center;
  color: #fff;
  font-family: "Golos Text", sans-serif;
  font-style: normal;
  font-weight: 600;
  font-size: 48px;
  line-height: 58px;
}

.problem-accent {
  position: relative;
  display: inline-block;
  color: var(--accent-color);
}

.problem-title-line {
  position: absolute;
  width: var(--title-line-w);
  height: auto;
  left: 50%;
  top: 100%;
  transform: translate(-50%, var(--title-line-y));
  pointer-events: none;
}

.problem-title-subtitle {
  margin: 0;
  width: 100%;
  text-align: center;
  color: #fff;
  font-family: "Golos Text", sans-serif;
  font-style: normal;
  font-weight: 500;
  font-size: 16px;
  line-height: 20px;
  opacity: 0.95;
}

/* ── Slider layout ── */
.slider-layout {
  --card-h: 460px;
  --container-card-h: var(--card-h);
  --container-extra: 80px;
  --stack-step: 20px;
  position: relative;
  display: flex;
  align-items: flex-start;
  justify-content: flex-end;
  width: 100%;
  overflow-x: clip;
}

.slider-title-wrap {
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 24px;
  width: 100%;
  min-height: 102px;
}

.slider-dots {
  position: absolute;
  left: 0;
  top: calc((var(--container-card-h) - 148px) / 2 + 20px);
  display: none;
  flex-direction: column;
  align-items: flex-start;
  gap: 8px;
  width: 8px;
  flex: none;
  z-index: 240;
}

.slider-dot {
  width: 8px;
  height: 20px;
  border: 0;
  border-radius: 8px;
  background: #fff;
  opacity: 0.2;
  cursor: pointer;
  transition: opacity 220ms ease, height 260ms ease;
  padding: 0;
}

.slider-dot[data-active="true"] {
  height: 36px;
  opacity: 1;
}

.slider-container {
  position: relative;
  width: min(1340px, calc(100% - 20px));
  height: auto;
  overflow: visible;
  flex: none;
  z-index: 1;
}

.slider-track {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 0;
  width: 100%;
  min-height: 100%;
  height: auto;
}

.problem-card {
  box-sizing: border-box;
  position: relative;
  width: 100%;
  height: var(--card-height, var(--card-h));
  border-radius: 52px;
  border: 2px solid var(--card-border-color);
  overflow: hidden;
  background: var(--card-bg-top), var(--card-bg-bottom);
  will-change: transform;
  isolation: isolate;
  scroll-snap-align: start;
}

.problem-card + .problem-card {
  margin-top: 20px;
}

.problem-math {
  position: absolute;
  inset: 0;
  background-image:
    repeating-linear-gradient(0deg, rgba(255, 255, 255, 0.2) 0 1px, transparent 1px 56px),
    repeating-linear-gradient(90deg, rgba(255, 255, 255, 0.2) 0 1px, transparent 1px 56px);
  background-size: 112px 112px;
  mix-blend-mode: hard-light;
  opacity: 0.1;
  transform: matrix(-1, 0, 0, 1, 0, 0);
  pointer-events: none;
}

.problem-effect {
  position: absolute;
  width: 276px;
  height: 276px;
  left: calc(50% - 138px + 390px);
  top: calc(50% - 138px + 42px);
  background: linear-gradient(180deg, #ce7f60 0%, #621321 100%);
  opacity: 0.5;
  filter: blur(100px);
  pointer-events: none;
}

.problem-image {
  position: absolute;
  width: 300px;
  height: 300px;
  right: 120px;
  top: 50%;
  transform: translateY(-50%);
  object-fit: contain;
  filter: drop-shadow(0 24px 44px rgba(0, 0, 0, 0.45));
}

.problem-root.is-consequences .problem-image {
  width: 276px;
  height: 276px;
  right: 132px;
}

.problem-side-image {
  position: absolute;
  display: none;
  width: 190px;
  height: auto;
  bottom: 42px;
  z-index: 3;
  pointer-events: none;
}

.problem-side-image.is-left {
  left: 52px;
}

.problem-side-image.is-right {
  right: 52px;
}

.problem-body {
  position: absolute;
  width: var(--body-width);
  min-height: var(--body-min-h);
  left: var(--body-left);
  top: 50%;
  transform: translateY(-50%);
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 24px;
}

.problem-copy-head {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: var(--copy-head-gap);
  width: var(--copy-width);
}

.problem-kicker {
  margin: 0;
  width: var(--copy-width);
  color: var(--kicker-color);
  font-family: "Golos Text", sans-serif;
  font-style: normal;
  font-weight: var(--kicker-weight);
  font-size: var(--kicker-size);
  line-height: var(--kicker-line-height);
  text-transform: var(--kicker-transform);
}

.problem-value {
  margin: 0;
  width: var(--copy-width);
  color: var(--value-color);
  font-family: "Golos Text", sans-serif;
  font-style: normal;
  font-weight: 700;
  font-size: var(--value-size);
  line-height: var(--value-line-height);
}

.problem-highlight {
  margin: 0;
  width: var(--copy-width);
  color: var(--highlight-color);
  font-family: "Golos Text", sans-serif;
  font-style: normal;
  font-weight: var(--highlight-weight);
  font-size: var(--highlight-size);
  line-height: var(--highlight-line-height);
}

.problem-copy-foot {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 24px;
  width: var(--copy-width);
}

.problem-description {
  margin: 0;
  width: var(--copy-width);
  color: #fff;
  font-family: "Golos Text", sans-serif;
  font-style: normal;
  font-weight: 400;
  font-size: var(--desc-size);
  line-height: var(--description-line-height);
  white-space: pre-line;
}

.problem-description-group {
  width: 100%;
  display: flex;
  flex-direction: column;
}

.problem-root.is-not-enough .problem-description-group {
  gap: 16px;
}

.problem-quote {
  margin: 0;
  width: var(--copy-width);
  border-radius: 16px;
  border: 1px solid var(--quote-border-color);
  background: var(--quote-bg);
  padding: 16px 20px;
