---
export const prerender = true;

import { NON_DEFAULT_LANGUAGES, type Lang } from "../../i18n/config";
import { localizedPath, resolveLang } from "../../i18n/utils";

export function getStaticPaths() {
  return NON_DEFAULT_LANGUAGES.map((lang) => ({
    params: { lang }
  }));
}

const lang = resolveLang(Astro.params.lang) as Lang;
const redirectPath = localizedPath(lang, "/privacy-policy");
---

<!doctype html>
<html lang={lang}>
  <head>
    <meta charset="utf-8" />
    <meta http-equiv="refresh" content={`0; url=${redirectPath}`} />
    <link rel="canonical" href={redirectPath} />
    <meta name="robots" content="noindex,follow" />
    <title>Redirecting...</title>
  </head>
  <body>
    <a href={redirectPath}>Redirecting...</a>
    <script is:inline define:vars={{ redirectPath }}>
      window.location.replace(redirectPath);
    </script>
  </body>
</html>

---
export const prerender = true;

import TeamSection from "../../components/TeamSection.astro";
import { createTeamStructuredData, teamMembers } from "../../content/team";
import { NON_DEFAULT_LANGUAGES, type Lang } from "../../i18n/config";
import { getTranslations, localizedPath, resolveLang } from "../../i18n/utils";
import BaseLayout from "../../layouts/BaseLayout.astro";

export function getStaticPaths() {
  return NON_DEFAULT_LANGUAGES.map((lang) => ({
    params: { lang }
  }));
}

const lang = resolveLang(Astro.params.lang) as Lang;
const t = getTranslations(lang);
const baseUrl = Astro.site ?? Astro.url;
const organizationUrl = new URL("/", baseUrl).toString();
const teamPageUrl = new URL(localizedPath(lang, "/team"), baseUrl).toString();
const teamStructuredData = createTeamStructuredData({
  lang,
  siteName: t.layout.siteName,
  teamPageUrl,
  organizationUrl
});
const teamMemberKeywords = teamMembers
  .map((member) => `${member.name}, ${member.role[lang]}, ${member.project[lang]}`)
  .join(", ");
const teamPageKeywords = [t.team.meta.keywords, teamMemberKeywords].filter(Boolean).join(", ");
---

<BaseLayout
  lang={lang}
  routePath="/team"
  theme="landing"
  title={t.team.meta.title}
  description={t.team.meta.description}
  keywords={teamPageKeywords}
>
  <Fragment slot="head">
    <script type="application/ld+json" is:inline set:html={JSON.stringify(teamStructuredData)} />
  </Fragment>
  <TeamSection lang={lang} headingLevel="h1" />
</BaseLayout>

