components.content:
---
import appStoreIconUrl from "../assets/images/herosection/app-store.svg?url";
import mascotImageUrl from "../assets/images/herosection/Illustrations.webp?url";
import playIconUrl from "../assets/images/herosection/play.svg?url";
import playMarketIconUrl from "../assets/images/herosection/play-market.svg?url";
import { DEFAULT_LANG, type Lang } from "../i18n/config";
import { getTranslations, resolveLang } from "../i18n/utils";
import { PLAY_MARKET_URL, APP_STORE_URL, VIDEO_STREAM_URL } from "../utils/constants";

type Props = {
  lang?: Lang;
  sectionId?: string;
  sectionClassName?: string;
  playMarketHref?: string;
  appStoreHref?: string;
  videoStreamUrl?: string;
};

const {
  lang = DEFAULT_LANG,
  sectionId = "about",
  sectionClassName,
  playMarketHref = PLAY_MARKET_URL,
  appStoreHref = APP_STORE_URL,
  videoStreamUrl = VIDEO_STREAM_URL
} = Astro.props as Props;

const activeLang = resolveLang(lang);
const content = getTranslations(activeLang).home.sections.aboutTikoncha;
---

<section id={sectionId} class:list={["about-cta-section scroll-mt-28", sectionClassName]}>
  <div class="about-cta-card">
    <div class="about-cta-copy">
      <h2 class="about-cta-title">
        <span>{content.title}</span>
        <span class="about-cta-title-accent">{content.titleAccent}</span>
      </h2>

      <p class="about-cta-description">{content.description}</p>

      <div class="about-cta-actions">
        <a href={playMarketHref} class="about-cta-action">
          <img src={playMarketIconUrl} alt="" loading="lazy" decoding="async" />
          <span>{content.playMarketLabel}</span>
        </a>
        <a href={appStoreHref} class="about-cta-action">
          <img src={appStoreIconUrl} alt="" loading="lazy" decoding="async" />
          <span>{content.appStoreLabel}</span>
        </a>
        <button
          type="button"
          class="about-cta-action"
          data-video-modal-trigger
          data-video-src={videoStreamUrl}
          aria-label={content.videoLabel}
        >
          <img src={playIconUrl} alt="" loading="lazy" decoding="async" />
          <span>{content.videoLabel}</span>
        </button>
      </div>
    </div>

    <div class="about-cta-visual" aria-hidden="true">
      <span class="about-cta-visual-glow"></span>
      <img
        src={mascotImageUrl}
        alt=""
        class="about-cta-illustration"
        width="900"
        height="1096"
        loading="lazy"
        decoding="async"
      />
    </div>
  </div>
</section>

<style>
  .about-cta-section {
    margin-top: 80px;
  }

  .about-cta-card {
    position: relative;
    overflow: hidden;
    min-height: 422px;
    border-radius: 52px;
    background: linear-gradient(241.25deg, rgba(255, 255, 255, 0.1) 0.52%, rgba(170, 157, 145, 0.1) 101.5%);
    box-shadow:
      inset 0 0 0 1px rgba(255, 255, 255, 0.05),
      0 18px 42px rgba(0, 0, 0, 0.28);
    isolation: isolate;
  }

  .about-cta-card::before {
    content: "";
    position: absolute;
    inset: 0;
    z-index: 0;
    pointer-events: none;
    background:
      radial-gradient(52% 120% at 79% 50%, rgba(247, 144, 9, 0.19) 0%, rgba(247, 144, 9, 0) 72%),
      linear-gradient(92deg, rgba(13, 14, 18, 0.4) 0%, rgba(13, 14, 18, 0.02) 52%, rgba(13, 14, 18, 0.34) 100%);
  }

  .about-cta-copy {
    position: relative;
    z-index: 2;
    width: 510px;
    max-width: calc(100% - 440px);
    padding: 69px 0 69px 80px;
    color: #fff;
  }

  .about-cta-title {
    margin: 0;
    display: flex;
    flex-direction: column;
    font-family: "Golos Text", sans-serif;
    font-size: clamp(30px, 2.9vw, 36px);
    line-height: 1.5;
    font-weight: 600;
    letter-spacing: 0;
  }

  .about-cta-title span {
    display: block;
    white-space: nowrap;
  }

  .about-cta-title-accent {
    color: #f79009;
  }

  .about-cta-description {
    margin: 24px 0 0;
    font-family: "Golos Text", sans-serif;
    font-size: clamp(18px, 1.8vw, 20px);
    line-height: 1.8;
    font-weight: 500;
    letter-spacing: 0.015em;
    color: rgba(255, 255, 255, 0.95);
  }

  .about-cta-actions {
    margin-top: 36px;
    display: flex;
    flex-wrap: nowrap;
    align-items: center;
    gap: 12px;
  }

  .about-cta-action {
    display: inline-flex;
    height: 44px;
    align-items: center;
    justify-content: center;
    gap: 8px;
    border-radius: 32px;
    background: rgba(255, 255, 255, 0.1);
    padding: 10px 24px 10px 16px;
    color: #fff;
    text-decoration: none;
    font-family: "Golos Text", sans-serif;
    font-size: 14px;
    line-height: 18px;
    font-weight: 500;
    white-space: nowrap;
    border: 0;
    cursor: pointer;
    transition:
      background-color 200ms ease,
      transform 200ms ease;
  }

  .about-cta-action img {
    width: 24px;
    height: 24px;
    flex-shrink: 0;
  }

  .about-cta-action:hover {
    background: rgba(255, 255, 255, 0.18);
    transform: translateY(-1px);
  }

  .about-cta-action:focus-visible {
    background: rgba(255, 255, 255, 0.18);
    transform: translateY(-1px);
    outline: 2px solid rgb(var(--color-secondary));
    outline-offset: 2px;
  }

  .about-cta-visual {
    position: absolute;
    right: 70px;
    top: 28px;
    z-index: 1;
    width: 292px;
    height: 356px;
    pointer-events: none;
  }

  .about-cta-visual-glow {
    position: absolute;
    left: 50%;
    top: 52%;
    width: 234px;
    height: 234px;
    border-radius: 50%;
    transform: translate(-50%, -50%);
    background:
      radial-gradient(circle, rgba(248, 208, 124, 0.28) 0%, rgba(248, 208, 124, 0.14) 46%, rgba(248, 208, 124, 0) 78%);
    filter: blur(8px);
  }

  .about-cta-illustration {
    position: absolute;
    inset: 0;
    width: 100%;
    height: 100%;
    object-fit: contain;
    object-position: center;
    filter: drop-shadow(0 14px 36px rgba(0, 0, 0, 0.4));
  }

  @media (max-width: 1320px) {
    .about-cta-card {
      border-radius: 42px;
    }

    .about-cta-copy {
      width: min(100%, 500px);
      max-width: calc(100% - 332px);
      padding-left: 44px;
    }

    .about-cta-visual {
      right: 20px;
      top: 34px;
      width: 272px;
      height: 332px;
    }
  }

  @media (max-width: 1080px) {
    .about-cta-card {
      min-height: 0;
      border-radius: 36px;
      padding: 34px 28px;
      display: grid;
      gap: 28px;
      justify-items: center;
    }

    .about-cta-copy {
      width: 100%;
      max-width: 720px;
      padding: 0;
    }

    .about-cta-title span {
      white-space: normal;
    }

    .about-cta-title {
      font-size: clamp(30px, 5.6vw, 36px);
    }

    .about-cta-description {
      font-size: clamp(17px, 2.5vw, 20px);
    }

    .about-cta-actions {
      margin-top: 24px;
      flex-wrap: wrap;
    }

    .about-cta-visual {
      position: relative;
      top: auto;
      right: auto;
      width: min(100%, 300px);
      height: auto;
      aspect-ratio: 317 / 386;
      justify-self: center;
    }
  }

  @media (min-width: 744px) and (max-width: 1366px) and (any-pointer: coarse) {
    .about-cta-card {
      min-height: 786px;
      border-radius: 52px;
      padding: 44px 0;
      display: grid;
      grid-template-rows: auto 1fr auto;
      align-items: start;
    }

    .about-cta-copy {
      width: 510px;
      max-width: calc(100% - 48px);
      padding: 0;
      margin: 0 auto;
      text-align: center;
    }

    .about-cta-title {
      font-size: 36px;
      line-height: 1.5;
    }

    .about-cta-title span {
      white-space: nowrap;
    }

    .about-cta-description {
      margin-top: 24px;
      font-size: 20px;
      line-height: 1.8;
      letter-spacing: 0.015em;
    }

    .about-cta-actions {
      position: relative;
      left: auto;
      bottom: auto;
      margin: 12px auto 0;
      transform: none;
      flex-wrap: nowrap;
      justify-content: center;
      gap: 12px;
      z-index: 2;
    }

    .about-cta-visual {
      position: relative;
      top: auto;
      left: auto;
      right: auto;
      width: 317px;
      height: 386px;
      margin: 20px auto 0;
      transform: none;
    }
  }

  @media (max-width: 743px) {
    .about-cta-card {
      min-height: 558px;
      border-radius: 32px;
      padding: 0;
      display: block;
    }

    .about-cta-copy {
      width: min(332px, calc(100% - 40px));
      max-width: none;
      padding: 32px 0 0;
      margin: 0 auto;
      position: static;
      text-align: center;
    }

    .about-cta-title {
      font-size: 20px;
      line-height: 24px;
      font-weight: 600;
    }

    .about-cta-title span {
      white-space: normal;
    }

    .about-cta-description {
      margin-top: 16px;
      font-size: 14px;
      line-height: 22px;
      letter-spacing: 0;
    }

    .about-cta-actions {
      position: absolute;
      left: 20px;
      right: 20px;
      bottom: 20px;
      margin-top: 0;
      margin-bottom: 0;
      display: grid;
      grid-template-columns: repeat(2, minmax(0, 1fr));
      gap: 8px;
      z-index: 2;
    }

    .about-cta-action {
      width: 100%;
      justify-content: center;
      padding: 10px 12px;
    }

    .about-cta-action:nth-child(3) {
      grid-column: 1 / -1;
    }

