<script>
  import { gsap } from "gsap";

  const initHeroAnimations = () => {
    const heroRoot = document.querySelector<HTMLElement>("[data-hero-root]");
    if (!heroRoot || heroRoot.dataset.animReady === "true") return;
    heroRoot.dataset.animReady = "true";

    const prefersReducedMotion = window.matchMedia("(prefers-reduced-motion: reduce)").matches;
    const chip = heroRoot.querySelector<HTMLElement>("[data-hero-chip]");
    const title = heroRoot.querySelector<HTMLElement>("[data-hero-title]");
    const subtitle = heroRoot.querySelector<HTMLElement>("[data-hero-subtitle]");
    const actions = heroRoot.querySelector<HTMLElement>("[data-hero-actions]");
    const image = heroRoot.querySelector<HTMLElement>("[data-hero-image]");
    const socialIcons = Array.from(heroRoot.querySelectorAll<HTMLElement>("[data-hero-social]"));

    const startFloatingIcons = () => {
      socialIcons.forEach((icon, index) => {
        if (icon.dataset.floatReady === "true") return;
        icon.dataset.floatReady = "true";

        const direction = index % 2 === 0 ? 1 : -1;
        const moveY = 14 + (index % 4) * 3;
        const moveX = 8 + (index % 3) * 2;
        const duration = 1.45 + (index % 3) * 0.22;

        gsap.to(icon, {
          y: -moveY * direction,
          duration,
          ease: "sine.inOut",
          repeat: -1,
          yoyo: true,
          delay: index * 0.08
        });

        gsap.to(icon, {
          x: moveX * direction,
          duration: duration * 1.35,
          ease: "sine.inOut",
          repeat: -1,
          yoyo: true,
          delay: index * 0.05
        });
      });
    };

    if (!prefersReducedMotion && chip && title && subtitle && actions && image && socialIcons.length > 0) {
      gsap
        .timeline({ defaults: { duration: 0.6, ease: "power2.out" } })
        .from(chip, { y: 10, opacity: 0 })
        .from(title, { y: 20, opacity: 0 }, "-=0.3")
        .from(subtitle, { y: 14, opacity: 0 }, "-=0.35")
        .from(actions, { y: 10, opacity: 0 }, "-=0.35")
        .from(image, { x: 18, opacity: 0 }, "-=0.45")
        .from(socialIcons, { scale: 0.9, opacity: 0, stagger: 0.05 }, "-=0.35")
        .add(startFloatingIcons);
    } else {
      startFloatingIcons();
    }

    // Safety fallback: if intro animation gets interrupted, keep CTA visible.
    window.setTimeout(() => {
      if (!actions) return;
      if (Number.parseFloat(getComputedStyle(actions).opacity) < 0.99) {
        actions.style.opacity = "1";
        actions.style.transform = "none";
      }
    }, 1400);
  };

  if (document.readyState === "loading") {
    document.addEventListener("DOMContentLoaded", initHeroAnimations, { once: true });
  } else {
    initHeroAnimations();
  }

  document.addEventListener("astro:page-load", initHeroAnimations);
</script>

