---
import blockedImageUrl from "../assets/images/not-enough/Blocked.webp?url";
import illustrationImageUrl from "../assets/images/not-enough/Illustrations.webp?url";
import leftSideImageUrl from "../assets/images/not-enough/image-left.webp?url";
import rightSideImageUrl from "../assets/images/not-enough/image-right.webp?url";
import noInternetImageUrl from "../assets/images/not-enough/No-Internet.webp?url";
import phoneStandImageUrl from "../assets/images/not-enough/Phone-Stand.webp?url";
import titleLineImageUrl from "../assets/images/not-enough/line.svg?url";
import { DEFAULT_LANG, type Lang } from "../i18n/config";
import { getTranslations, resolveLang } from "../i18n/utils";
import StackedSliderSection, { type SlideItem } from "./StackedSliderSection.astro";

const slideImages = [blockedImageUrl, noInternetImageUrl, phoneStandImageUrl, illustrationImageUrl];

type Props = {
  lang?: Lang;
  sectionId?: string;
  title?: string;
  titleAccent?: string;
  subtitle?: string;
  slides?: SlideItem[];
  sliderAriaLabel?: string;
  dotsAriaLabel?: string;
  slideAriaSuffix?: string;
  sourceLabel?: string;
  sectionClassName?: string;
  variantClassName?: string;
};

const {
  lang = DEFAULT_LANG,
  sectionId = "not-enough",
  title,
  titleAccent,
  subtitle,
  slides,
  sliderAriaLabel,
  dotsAriaLabel,
  slideAriaSuffix,
  sourceLabel,
  sectionClassName,
  variantClassName = "is-not-enough"
} = Astro.props as Props;

const activeLang = resolveLang(lang);
const content = getTranslations(activeLang).home.sections.notEnough;

const resolvedSlides: SlideItem[] =
  slides ??
  content.slides.map((slide, index) => ({
    ...slide,
    imageUrl: slideImages[index] ?? slideImages[0],
    leftImageUrl: index === 3 ? leftSideImageUrl : undefined,
    rightImageUrl: index === 3 ? rightSideImageUrl : undefined
  }));
---

<StackedSliderSection
  sectionId={sectionId}
  title={title ?? content.title}
  titleAccent={titleAccent ?? content.titleAccent}
  subtitle={subtitle ?? content.subtitle}
  slides={resolvedSlides}
  sectionClassName={sectionClassName}
  variantClassName={variantClassName}
  titleLineImageUrl={titleLineImageUrl}
  sliderAriaLabel={sliderAriaLabel ?? content.sliderAriaLabel}
  dotsAriaLabel={dotsAriaLabel ?? content.dotsAriaLabel}
  slideAriaSuffix={slideAriaSuffix ?? content.slideAriaSuffix}
  sourceLabel={sourceLabel ?? content.sourceLabel}
/>

---
import type { PolicyContent } from "../content/policies";

type Props = {
  content: PolicyContent;
  homeHref: string;
  tocLabel?: string;
  backLabel?: string;
  contactEmail?: string;
  contactPhone?: string;
};

const {
  content,
  homeHref,
  tocLabel = "Mundarija",
  backLabel = "Bosh sahifaga qaytish",
  contactEmail = "info@tikoncha.uz",
  contactPhone = "+998 97 270 70 07"
} = Astro.props as Props;
---

<section class="min-h-screen bg-[#090b10] py-8 lg:py-12">
  <div class="mx-auto w-full max-w-[1280px] px-4 sm:px-6 lg:px-8">
    <div class="grid gap-8 lg:grid-cols-12">
      <aside class="hidden lg:col-span-3 lg:block">
        <div class="sticky top-24 rounded-2xl border border-[#2b2b31] bg-[#11141b] p-6 shadow-[0_14px_30px_rgba(0,0,0,0.25)]">
          <h2 class="mb-4 text-sm font-semibold uppercase tracking-wide text-gray-100">{tocLabel}</h2>
          <nav class="space-y-1">
            {
              content.sections.map((section) => (
                <a
                  href={`#${section.id}`}
                  class="block rounded-lg px-3 py-2.5 text-sm text-[#b7bec9] transition-colors hover:bg-white/10 hover:text-gray-100"
                >
                  {section.title}
                </a>
              ))
            }
          </nav>
        </div>
      </aside>

      <main class="lg:col-span-9">
        <div class="mb-8 rounded-3xl border border-[#2b2b31] bg-gradient-to-br from-secondary/20 via-secondary/10 to-[#121821] p-8 lg:p-12">
          <h1 class="text-3xl font-bold text-gray-100 lg:text-4xl">{content.title}</h1>
          <p class="mt-6 text-lg leading-relaxed text-[#c7cdd7]">{content.intro}</p>
        </div>

        <div class="mb-8 lg:hidden">
          <details class="overflow-hidden rounded-2xl border border-[#2b2b31] bg-[#11141b] shadow-[0_14px_30px_rgba(0,0,0,0.25)]">
            <summary class="cursor-pointer px-6 py-4 text-sm font-semibold text-gray-100">{tocLabel}</summary>
            <nav class="space-y-1 px-6 pb-4">
              {
                content.sections.map((section) => (
                  <a
                    href={`#${section.id}`}
                    class="block rounded-lg px-3 py-2.5 text-sm text-[#b7bec9] transition-colors hover:bg-white/10 hover:text-gray-100"
                  >
                    {section.title}
                  </a>
                ))
              }
            </nav>
          </details>
        </div>

        <div class="space-y-6">
          {
            content.sections.map((section) => (
              <section
                id={section.id}
                class="scroll-mt-24 rounded-2xl border border-[#2b2b31] bg-[#12161f] p-6 shadow-[0_14px_30px_rgba(0,0,0,0.2)] lg:p-8"
              >
                <h2 class="text-xl font-bold text-gray-100 lg:text-2xl">{section.title}</h2>
                {section.description && (
                  <p class="mt-4 leading-relaxed text-[#c7cdd7]">{section.description}</p>
                )}
                {section.items && section.items.length > 0 && (
                  <ul class="mt-4 list-disc space-y-3 pl-5">
                    {section.items.map((item) => <li class="leading-relaxed text-[#b7bec9]">{item}</li>)}
                  </ul>
                )}
                {section.note && (
                  <div class="mt-4 rounded-xl border-l-4 border-secondary bg-secondary/15 p-4">
                    <p class="text-sm italic leading-relaxed text-[#d6dfd8]">{section.note}</p>
                  </div>
                )}
              </section>
            ))
          }
        </div>

        <div class="mt-8 rounded-2xl bg-gradient-to-r from-secondary to-secondary/80 p-8 text-white">
          <div class="flex flex-col gap-6 lg:flex-row lg:items-center lg:justify-between">
            <div>
              <p class="text-xl font-bold">{contactEmail}</p>
              <p class="mt-1 text-sm text-white/85">{contactPhone}</p>
            </div>
            <a
              href={homeHref}
              class="inline-flex items-center justify-center rounded-xl bg-white px-6 py-3 font-semibold text-primary transition-colors hover:bg-white/90"
            >
              {backLabel}
            </a>
          </div>
        </div>
      </main>
    </div>
  </div>
</section>

---
import barChartImageUrl from "../assets/images/problemsection/Bar-Chart.webp?url";
import clepsydraImageUrl from "../assets/images/problemsection/Clepsydra.webp?url";
import phoneScrollingImageUrl from "../assets/images/problemsection/Phone-Scrolling.webp?url";
import puppetImageUrl from "../assets/images/problemsection/Puppet.webp?url";
import socialMediaAppImageUrl from "../assets/images/problemsection/Social-media-app.webp?url";
import titleLineImageUrl from "../assets/images/problemsection/titleline.svg?url";
import { DEFAULT_LANG, type Lang } from "../i18n/config";
import { getTranslations, resolveLang } from "../i18n/utils";
import StackedSliderSection, { type SlideItem } from "./StackedSliderSection.astro";

const slideImages = [
  puppetImageUrl,
  socialMediaAppImageUrl,
  clepsydraImageUrl,
  phoneScrollingImageUrl,
  barChartImageUrl
];

type Props = {
  lang?: Lang;
  sectionId?: string;
  title?: string;
  titleAccent?: string;
  subtitle?: string;
  slides?: SlideItem[];
  sliderAriaLabel?: string;
  dotsAriaLabel?: string;
  slideAriaSuffix?: string;
  sourceLabel?: string;
  sectionClassName?: string;
  variantClassName?: string;
};

const {
  lang = DEFAULT_LANG,
  sectionId = "problem",
  title,
  titleAccent,
  subtitle,
  slides,
  sliderAriaLabel,
  dotsAriaLabel,
  slideAriaSuffix,
  sourceLabel,
  sectionClassName,
  variantClassName
} = Astro.props as Props;

const activeLang = resolveLang(lang);
const content = getTranslations(activeLang).home.sections.problem;

const resolvedSlides: SlideItem[] =
  slides ??
  content.slides.map((slide, index) => ({
    ...slide,
    imageUrl: slideImages[index] ?? slideImages[0]
  }));
---

<StackedSliderSection
  sectionId={sectionId}
  title={title ?? content.title}
  titleAccent={titleAccent ?? content.titleAccent}
  subtitle={subtitle ?? content.subtitle}
  slides={resolvedSlides}
  sectionClassName={sectionClassName}
  variantClassName={variantClassName}
  titleLineImageUrl={titleLineImageUrl}
  sliderAriaLabel={sliderAriaLabel ?? content.sliderAriaLabel}
  dotsAriaLabel={dotsAriaLabel ?? content.dotsAriaLabel}
  slideAriaSuffix={slideAriaSuffix ?? content.slideAriaSuffix}
  sourceLabel={sourceLabel ?? content.sourceLabel}
/>

---
import callIconUrl from "../assets/images/footer/Call-Ringing.svg?url";
import emailIconUrl from "../assets/images/footer/email.svg?url";
import instagramIconUrl from "../assets/images/footer/Instagram.svg?url";
import locationIconUrl from "../assets/images/footer/Location.svg?url";
import telegramIconUrl from "../assets/images/footer/Telegram.svg?url";
import { type Lang } from "../i18n/config";
import { getTranslations, localizedPath } from "../i18n/utils";
import { INSTAGRAM_URL, TELEGRAM_URL } from "../utils/constants";
import { withBase } from "../utils/url";

type Props = {
  lang: Lang;
};

const { lang } = Astro.props as Props;
const t = getTranslations(lang);
const logoSrc = withBase("/logo.png");

const homePath = localizedPath(lang);
const privacyPath = localizedPath(lang, "/privacy-policy");
const termsPath = localizedPath(lang, "/terms");
const featuresPath = `${homePath}#how-does-it-work`;
const subscriptionPath = `${homePath}#hero`;
const aboutPath = `${homePath}#about`;
const teamPath = localizedPath(lang, "/team");
const phoneLink = `tel:${t.layout.footer.phone.replace(/\s+/g, "")}`;
const emailLink = `mailto:${t.layout.footer.email}`;
---

<footer class="site-footer text-white">
  <div class="site-footer__top">
    <div class="site-footer__brand">
      <a href={homePath} class="site-footer__logo-link" aria-label={t.layout.siteName}>
        <img
          src={logoSrc}
          alt={t.layout.siteName}
          class="site-footer__logo"
          width="728"
          height="144"
          loading="lazy"
          decoding="async"
        />
