      <p class="site-footer__tagline">{t.layout.footer.tagline}</p>

      <div class="site-footer__socials" aria-label={t.layout.aria.socialLinks}>
        <a
          href={INSTAGRAM_URL}
          class="site-footer__social-link"
          aria-label="Instagram"
          target="_blank"
          rel="noopener noreferrer"
        >
          <img src={instagramIconUrl} alt="" loading="lazy" decoding="async" />
