        </li>
        <li>
          <a href={subscriptionPath} class="site-footer__link">
            {t.layout.footer.companyLinks.subscription}
          </a>
        </li>
        <li>
          <a href={aboutPath} class="site-footer__link">
            {t.layout.footer.companyLinks.about}
          </a>
        </li>
