---
import appStoreIconUrl from "../assets/images/herosection/app-store.svg?url";
import playMarketIconUrl from "../assets/images/herosection/play-market.svg?url";
import instagramIconUrl from "../assets/images/footer/Instagram.svg?url";
import telegramIconUrl from "../assets/images/footer/Telegram.svg?url";
import { LANGUAGES, type Lang } from "../i18n/config";
import { getTranslations, localizedPath } from "../i18n/utils";
import { PLAY_MARKET_URL, APP_STORE_URL, INSTAGRAM_URL, TELEGRAM_URL } from "../utils/constants";
import { withBase } from "../utils/url";

type Props = {
  lang: Lang;
  routePath?: string;
};

const { lang, routePath = "/" } = Astro.props as Props;
const t = getTranslations(lang);
const logoSrc = withBase("/logo.png");

const homePath = localizedPath(lang);
const homePathWithSlash = homePath.endsWith("/") ? homePath : `${homePath}/`;
const teamPath = localizedPath(lang, "/team");
const phoneLink = `tel:${t.layout.footer.phone.replace(/\s+/g, "")}`;

const navItems = [
  { key: "issue", href: `${homePathWithSlash}#problem`, label: t.layout.nav.issue, sectionId: "problem" },
  { key: "consequences", href: `${homePathWithSlash}#consequences`, label: t.layout.nav.consequences, sectionId: "consequences" },
  { key: "leadingCountries", href: `${homePathWithSlash}#countries`, label: t.layout.nav.leadingCountries, sectionId: "countries" },
  { key: "about", href: `${homePathWithSlash}#about`, label: t.layout.nav.about, sectionId: "about" },
  { key: "team", href: teamPath, label: t.layout.nav.team, sectionId: undefined }
];
---

<header
  data-site-header
  data-home-path={homePath}
  data-team-path={teamPath}
  class="sticky top-0 z-40 text-mist transition-colors duration-200"
>
  <div class="site-header-bar mx-auto flex h-[72px] w-full max-w-[1360px] items-center justify-between px-5 lg:px-6">
    <a href={homePath} class="inline-flex items-center">
      <img
        src={logoSrc}
        alt={t.layout.siteName}
        class="h-9 w-auto"
        width="728"
        height="144"
        loading="eager"
        decoding="async"
      />
    </a>

    <div class="flex items-center gap-3 sm:gap-5">
      <nav class="site-header-desktop-nav hidden items-center gap-11 text-[16px] font-medium text-mist/95 lg:flex">
        {
          navItems.map((item) => (
            <a
              href={item.href}
              class="site-header-link whitespace-nowrap transition hover:text-secondary"
              data-site-nav-link
              data-nav-key={item.key}
              data-section-id={item.sectionId}
            >
              {item.label}
            </a>
          ))
        }
      </nav>

      <div class="site-header-desktop-divider hidden h-5 w-px bg-white/20 lg:block"></div>

      <details class="group relative">
        <summary
          class="flex cursor-pointer list-none items-center gap-1.5 text-sm font-medium text-mist/95"
          aria-label={t.layout.languageSwitcherLabel}
        >
          <svg viewBox="0 0 24 24" class="size-4 fill-none stroke-current" aria-hidden="true">
            <circle cx="12" cy="12" r="9" stroke-width="1.8"></circle>
            <path d="M3 12h18M12 3c2.8 2.7 2.8 15.3 0 18M12 3c-2.8 2.7-2.8 15.3 0 18" stroke-width="1.4"></path>
          </svg>
          <span>{lang.toUpperCase()}</span>
          <svg viewBox="0 0 24 24" class="size-3.5 fill-none stroke-current transition group-open:rotate-180" aria-hidden="true">
            <path d="M6 9l6 6 6-6" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"></path>
          </svg>
        </summary>

        <ul class="absolute right-0 top-8 min-w-28 rounded-xl border border-white/15 bg-[#111318] p-1.5 shadow-xl">
          {
            LANGUAGES.map((locale) => (
              <li>
                <a
                  href={localizedPath(locale, routePath)}
                  title={t.layout.languages[locale]}
                  class:list={[
                    "block rounded-lg px-3 py-1.5 text-xs font-semibold transition",
                    locale === lang ? "bg-secondary text-primary" : "text-mist/90 hover:bg-white/10"
                  ]}
                >
                  {locale.toUpperCase()}
                </a>
              </li>
            ))
          }
        </ul>
      </details>

      <button
        type="button"
        class="site-header-menu"
        data-site-menu-toggle
        data-label-open={t.layout.aria.openMenu}
        data-label-close={t.layout.aria.closeMenu}
        aria-label={t.layout.aria.openMenu}
        aria-expanded="false"
        aria-controls="site-tablet-menu"
      >
        <svg class="site-header-menu-glyph site-header-menu-glyph--burger" viewBox="0 0 24 24" aria-hidden="true">
          <path d="M4 7h16M4 12h16M4 17h16" stroke-linecap="round" stroke-width="2"></path>
        </svg>
        <svg class="site-header-menu-glyph site-header-menu-glyph--close" viewBox="0 0 24 24" aria-hidden="true">
          <path d="M6 6l12 12M18 6L6 18" stroke-linecap="round" stroke-width="2"></path>
        </svg>
      </button>
    </div>
  </div>

  <div id="site-tablet-menu" class="site-header-panel" data-site-menu-panel aria-hidden="true">
    <div class="site-header-panel-inner">
      <nav class="site-header-panel-nav" aria-label={t.layout.nav.issue}>
        {
          navItems.map((item) => (
            <a
              href={item.href}
              class="site-header-panel-link"
              data-site-menu-link
              data-site-nav-link
              data-nav-key={item.key}
              data-section-id={item.sectionId}
            >
              {item.label}
            </a>
          ))
        }
      </nav>

      <section class="site-header-panel-contact" aria-label={t.layout.footer.contactTitle}>
        <p class="site-header-panel-contact-title">{t.layout.footer.contactTitle}</p>
        <a href={phoneLink} class="site-header-panel-phone" data-site-menu-link>
          {t.layout.footer.phone}
        </a>

        <div class="site-header-panel-socials">
          <a
            href={INSTAGRAM_URL}
            class="site-header-panel-social-link"
            aria-label="Instagram"
            target="_blank"
            rel="noopener noreferrer"
            data-site-menu-link
          >
            <img src={instagramIconUrl} alt="" loading="lazy" decoding="async" />
          </a>
          <a
            href={TELEGRAM_URL}
            class="site-header-panel-social-link"
            aria-label="Telegram"
            target="_blank"
            rel="noopener noreferrer"
            data-site-menu-link
          >
            <img src={telegramIconUrl} alt="" loading="lazy" decoding="async" />
          </a>
        </div>

        <div class="site-header-panel-store-row">
          <a
            href={PLAY_MARKET_URL}
            class="site-header-panel-store-link"
            target="_blank"
            rel="noopener noreferrer"
            data-site-menu-link
          >
            <img src={playMarketIconUrl} alt="" loading="lazy" decoding="async" />
            <span>{t.home.hero.playMarketLabel}</span>
          </a>
          <a href={APP_STORE_URL} class="site-header-panel-store-link" data-site-menu-link>
            <img src={appStoreIconUrl} alt="" loading="lazy" decoding="async" />
            <span>{t.home.hero.appStoreLabel}</span>
          </a>
        </div>
      </section>

      <div class="site-header-panel-bottom">
        <p>{t.layout.footer.copyright}</p>
      </div>
    </div>
  </div>
</header>

<script>
  const header = document.querySelector<HTMLElement>("[data-site-header]");
  const menuToggle = header?.querySelector<HTMLButtonElement>("[data-site-menu-toggle]");
  const menuPanel = header?.querySelector<HTMLElement>("[data-site-menu-panel]");
  const languageSwitcher = header?.querySelector<HTMLDetailsElement>("details");
  const menuLinks = Array.from(header?.querySelectorAll<HTMLAnchorElement>("[data-site-menu-link]") ?? []);
  const navLinks = Array.from(header?.querySelectorAll<HTMLAnchorElement>("[data-site-nav-link]") ?? []);
  const TABLET_MEDIA_QUERY =
    "(max-width: 743px), (min-width: 744px) and (max-width: 1366px) and (any-pointer: coarse)";
  const menuViewportQuery = window.matchMedia(TABLET_MEDIA_QUERY);
  const homePath = header?.dataset.homePath ?? "/";
  const teamPath = header?.dataset.teamPath ?? "/team";

  const normalizePath = (path: string) => {
    if (!path) return "/";
    if (path.length > 1 && path.endsWith("/")) return path.slice(0, -1);
    return path;
  };

  const navLinksByKey = new Map<string, HTMLAnchorElement[]>();
  const sectionByKey = new Map<string, HTMLElement>();

  navLinks.forEach((link) => {
    const key = link.dataset.navKey;
    if (!key) return;

    const existing = navLinksByKey.get(key) ?? [];
    existing.push(link);
    navLinksByKey.set(key, existing);

    const sectionId = link.dataset.sectionId;
    if (sectionId) {
      const section = document.getElementById(sectionId);
      if (section) sectionByKey.set(key, section);
    }
  });

  const closeMenu = () => {
    if (!header || !menuToggle || !menuPanel) return;
    header.dataset.menuOpen = "false";
    document.body.classList.remove("site-menu-open");
    menuToggle.setAttribute("aria-expanded", "false");
    menuToggle.setAttribute("aria-label", menuToggle.dataset.labelOpen ?? "Open menu");
    menuPanel.setAttribute("aria-hidden", "true");
  };

  const toggleMenu = () => {
    if (!header || !menuToggle || !menuPanel || !menuViewportQuery.matches) return;
    const isOpen = header.dataset.menuOpen === "true";
    if (isOpen) {
      closeMenu();
      return;
    }
    header.dataset.menuOpen = "true";
    document.body.classList.add("site-menu-open");
    menuToggle.setAttribute("aria-expanded", "true");
    menuToggle.setAttribute("aria-label", menuToggle.dataset.labelClose ?? "Close menu");
    menuPanel.setAttribute("aria-hidden", "false");
  };

  const closeLanguageSwitcher = () => {
    if (!languageSwitcher) return;
    languageSwitcher.open = false;
  };

  const setActiveLink = (activeKey: string | null) => {
    navLinksByKey.forEach((links, key) => {
      const isActive = key === activeKey;
      links.forEach((link) => {
        link.classList.toggle("is-active", isActive);
        if (isActive) {
          link.setAttribute("aria-current", key === "team" ? "page" : "location");
        } else {
          link.removeAttribute("aria-current");
        }
      });
    });
  };

  const resolveHomeActiveKey = (): string | null => {
    if (sectionByKey.size === 0) return null;

    const offset = (header?.offsetHeight ?? 72) + 24;
    let keyPassedTop: string | null = null;
    let maxTop = Number.NEGATIVE_INFINITY;

    sectionByKey.forEach((section, key) => {
      const top = section.getBoundingClientRect().top;
      if (top <= offset && top > maxTop) {
        maxTop = top;
        keyPassedTop = key;
      }
    });

    if (keyPassedTop) return keyPassedTop;
    return null;
  };

  const syncActiveLink = () => {
    const currentPath = normalizePath(window.location.pathname);
    const normalizedHomePath = normalizePath(homePath);
    const normalizedTeamPath = normalizePath(teamPath);

    if (currentPath === normalizedTeamPath) {
      setActiveLink("team");
      return;
    }

    if (currentPath !== normalizedHomePath) {
      setActiveLink(null);
      return;
    }

    setActiveLink(resolveHomeActiveKey());
  };

  const syncHeaderState = () => {
    if (!header) return;
    header.classList.toggle("is-stuck", window.scrollY > 8);
  };

  let activeSyncFrame = 0;
  const requestActiveSync = () => {
    if (activeSyncFrame !== 0) return;
    activeSyncFrame = window.requestAnimationFrame(() => {
      activeSyncFrame = 0;
      syncActiveLink();
    });
  };

  syncHeaderState();
  syncActiveLink();
  window.addEventListener("scroll", syncHeaderState, { passive: true });
  window.addEventListener("scroll", requestActiveSync, { passive: true });
  window.addEventListener("hashchange", syncActiveLink);
  window.addEventListener("popstate", syncActiveLink);

  menuToggle?.addEventListener("click", () => {
    toggleMenu();
  });

  menuLinks.forEach((link) => {
    link.addEventListener("click", (event) => {
      if (link.getAttribute("href") === "#") event.preventDefault();
      closeMenu();
      closeLanguageSwitcher();
    });
  });

  navLinks.forEach((link) => {
    link.addEventListener("click", () => {
      window.requestAnimationFrame(syncActiveLink);
    });
  });

  document.addEventListener("click", (event) => {
    if (!header || !menuToggle || !menuPanel) return;
    const target = event.target;
    if (!(target instanceof Node)) return;
    if (!header.contains(target)) closeMenu();
    if (languageSwitcher?.open && !languageSwitcher.contains(target)) {
      closeLanguageSwitcher();
    }
  });

  window.addEventListener("keydown", (event) => {
    if (event.key === "Escape") {
      closeMenu();
      closeLanguageSwitcher();
    }
  });

  menuViewportQuery.addEventListener("change", () => {
    if (!menuViewportQuery.matches) closeMenu();
  });

  closeMenu();
</script>

