  }

  .site-header-panel-store-row {
    width: min(362px, 100%);
    display: grid;
    grid-template-columns: repeat(2, minmax(0, 1fr));
    gap: 8px;
  }

  .site-header-panel-store-link {
    min-height: 44px;
    border-radius: 32px;
    background: rgba(255, 255, 255, 0.1);
    display: inline-flex;
