    transform: translateY(-1px);
  }

  .site-header-panel-store-link:focus-visible {
    background: rgba(255, 255, 255, 0.18);
    transform: translateY(-1px);
    outline: 2px solid rgb(var(--color-secondary));
    outline-offset: 2px;
  }

  .site-header-panel-bottom {
    margin-top: auto;
    min-height: 64px;
    border-top: 1px solid rgba(255, 255, 255, 0.14);
    display: flex;
    align-items: center;
    justify-content: center;
    text-align: center;
  }

  .site-header-panel-bottom p {
    margin: 0;
    color: #dcdddf;
    font-family: "Golos Text", sans-serif;
    font-size: 12px;
    line-height: 14px;
    font-weight: 400;
  }

  @media (max-width: 743px), (min-width: 744px) and (max-width: 1366px) and (any-pointer: coarse) {
    .site-header-desktop-nav,
    .site-header-desktop-divider {
      display: none !important;
    }

    :global(body.site-menu-open) {
      overflow: hidden;
    }

    .site-header-menu {
      display: inline-flex;
    }

    .site-header-panel {
