---
export type SlideItem = {
  title: string;
  description: string;
  descriptionAccent?: string;
  source?: string;
  sourceHref?: string;
  imageUrl: string;
  imageAlt: string;
  highlight?: string;
  quote?: string;
  sourceInQuote?: boolean;
  hideSource?: boolean;
  value?: string;
  bodyHeight?: number;
  cardHeight?: number;
  sourceWidth?: number;
  descriptionSize?: number;
  leftImageUrl?: string;
  leftImageAlt?: string;
  rightImageUrl?: string;
  rightImageAlt?: string;
  cardClassName?: string;
};

type Props = {
  sectionId?: string;
  title?: string;
  titleAccent?: string;
  accentFirst?: boolean;
  subtitle?: string;
  slides?: SlideItem[];
  titleLineImageUrl: string;
  sliderAriaLabel?: string;
  dotsAriaLabel?: string;
  slideAriaSuffix?: string;
  sourceLabel?: string;
  sectionClassName?: string;
  variantClassName?: string;
};

const {
  sectionId = "slider-section",
  title = "",
  titleAccent = "",
  accentFirst = false,
  subtitle = "",
  slides = [],
  titleLineImageUrl,
  sliderAriaLabel = "Statistika slideri",
  dotsAriaLabel = "Slides navigation",
  slideAriaSuffix = "slaydi",
  sourceLabel = "Manba:",
  sectionClassName = "",
  variantClassName = ""
} = Astro.props as Props;

const splitDescriptionByAccent = (slide: SlideItem) => {
  if (!slide.descriptionAccent) return null;

  const index = slide.description.indexOf(slide.descriptionAccent);
  if (index < 0) return null;

  return {
    before: slide.description.slice(0, index),
    accent: slide.descriptionAccent,
    after: slide.description.slice(index + slide.descriptionAccent.length)
  };
};

const splitDescriptionLines = (description: string) =>
  description
    .split("\n")
    .map((line) => line.trim())
    .filter(Boolean);

const isNotEnoughVariant = variantClassName.split(" ").includes("is-not-enough");
---

<section
  id={sectionId}
  class:list={["problem-section scroll-mt-28 text-mist", sectionClassName]}
>
  <div class="problem-inner">
    <div class:list={["problem-root", variantClassName]} data-problem-slider>
    <div class="slider-title-wrap">
      <h2 class="problem-title">
        {
          accentFirst ? (
            <>
              <span class="problem-accent">
                {titleAccent}
                <img
                  src={titleLineImageUrl}
                  alt=""
                  class="problem-title-line"
                  loading="lazy"
                  decoding="async"
                />
              </span>
              <span> {title}</span>
            </>
          ) : (
            <>
              <span>{title} </span>
              <span class="problem-accent">
                {titleAccent}
                <img
                  src={titleLineImageUrl}
                  alt=""
                  class="problem-title-line"
                  loading="lazy"
                  decoding="async"
                />
              </span>
            </>
          )
        }
      </h2>
      <p class="problem-title-subtitle">{subtitle}</p>
    </div>

    <div class="slider-layout" data-slider-scroll-zone>
      <div class="slider-dots" aria-label={dotsAriaLabel}>
        {
          slides.map((slide, index) => (
            <button
              type="button"
              class="slider-dot"
              data-slider-dot
              data-active={index === 0 ? "true" : "false"}
              aria-label={`${slide.title} ${slideAriaSuffix}`}
              aria-controls={`${sectionId}-slide-${index}`}
              aria-current={index === 0 ? "true" : "false"}
            >
            </button>
          ))
        }
      </div>

      <div
        class="slider-container focus:outline-none focus-visible:ring-2 focus-visible:ring-[#FDB022]"
        data-slider-container
        tabindex="0"
        aria-label={sliderAriaLabel}
      >
        <div class="slider-track" data-slider-track>
          {
            slides.map((slide, index) => {
              const descriptionParts = splitDescriptionByAccent(slide);
              const isNotEnoughOutroCard = slide.cardClassName?.split(" ").includes("is-not-enough-outro") ?? false;
              const shouldRenderDescriptionLines = isNotEnoughVariant && !isNotEnoughOutroCard;
              const descriptionLines = shouldRenderDescriptionLines
                ? splitDescriptionLines(slide.description)
                : [];

              return (
                <article
                  id={`${sectionId}-slide-${index}`}
                  class:list={["problem-card", slide.cardClassName]}
                  data-slider-slide
                  data-custom-card-height={slide.cardHeight ? String(slide.cardHeight) : undefined}
                  aria-hidden={index === 0 ? "false" : "true"}
                  style={`--stack-i:${index}; --body-h:${slide.bodyHeight ?? 252}px; --source-w:${slide.sourceWidth ?? 320}px; --desc-size:${slide.descriptionSize ?? 16}px;${
                    slide.cardHeight ? ` --card-height:${slide.cardHeight}px;` : ""
                  }`}
                >
                <div aria-hidden="true" class="problem-math"></div>
                <div aria-hidden="true" class="problem-effect"></div>

                <img
                  src={slide.imageUrl}
                  alt={slide.imageAlt}
                  class="problem-image slider-card-image"
                  loading="lazy"
                  decoding="async"
                />
                {slide.leftImageUrl && (
                  <img
                    src={slide.leftImageUrl}
                    alt={slide.leftImageAlt ?? ""}
                    class="problem-side-image is-left"
                    loading="lazy"
                    decoding="async"
                  />
                )}
                {slide.rightImageUrl && (
                  <img
                    src={slide.rightImageUrl}
                    alt={slide.rightImageAlt ?? ""}
                    class="problem-side-image is-right"
                    loading="lazy"
                    decoding="async"
                  />
                )}

                <div class="problem-body slider-card-body">
                  <div class="problem-copy-head">
                    <p class="problem-kicker">{slide.title}</p>
                    {slide.value && <p class="problem-value">{slide.value}</p>}
                    {slide.highlight && <p class="problem-highlight">{slide.highlight}</p>}
                  </div>

                  <div class="problem-copy-foot">
                    {descriptionLines.length > 1 ? (
                      <div class="problem-description-group">
                        {descriptionLines.map((line) => (
                          <p class="problem-description">{line}</p>
                        ))}
                      </div>
                    ) : (
                      <p class="problem-description">
                        {descriptionParts ? (
                          <>
                            {descriptionParts.before}
                            <span class="problem-description-accent">{descriptionParts.accent}</span>
                            {descriptionParts.after}
                          </>
                        ) : (
                          slide.description
                        )}
                      </p>
                    )}
                    {slide.quote && (
                      <div class:list={["problem-quote-box", slide.sourceInQuote && "is-source-card"]}>
                        <p class="problem-quote">{slide.quote}</p>
                        {slide.sourceInQuote && !slide.hideSource && slide.source && slide.sourceHref && (
                          <div class="problem-source-chip problem-source-chip-in-quote">
                            <span class="problem-source-label">{sourceLabel}</span>
                            <a
                              href={slide.sourceHref}
                              target="_blank"
                              rel="noreferrer"
                              class="problem-source-link"
                            >
                              <span>{slide.source}</span>
                              <svg viewBox="0 0 24 24" class="problem-source-icon" aria-hidden="true">
                                <path
                                  d="M14 5h5v5M10 14L19 5M19 13v4a2 2 0 0 1-2 2h-10a2 2 0 0 1-2-2v-10a2 2 0 0 1 2-2h4"
                                  stroke-linecap="round"
                                  stroke-linejoin="round"
                                ></path>
                              </svg>
                            </a>
                          </div>
                        )}
                      </div>
                    )}

                    {!slide.hideSource && slide.source && slide.sourceHref && (
                      <div class:list={["problem-source-chip", slide.sourceInQuote && "is-hidden"]}>
                        <span class="problem-source-label">{sourceLabel}</span>
                        <a
                          href={slide.sourceHref}
                          target="_blank"
                          rel="noreferrer"
                          class="problem-source-link"
                        >
                          <span>{slide.source}</span>
                          <svg viewBox="0 0 24 24" class="problem-source-icon" aria-hidden="true">
                            <path
                              d="M14 5h5v5M10 14L19 5M19 13v4a2 2 0 0 1-2 2h-10a2 2 0 0 1-2-2v-10a2 2 0 0 1 2-2h4"
                              stroke-linecap="round"
                              stroke-linejoin="round"
                            ></path>
                          </svg>
                        </a>
                      </div>
                    )}
                  </div>
                </div>

                {slide.quote && slide.sourceInQuote && !slide.hideSource && slide.source && slide.sourceHref && (
                  <div class="problem-world-mobile-desc slider-card-world-desc">
                    <p class="problem-world-mobile-quote">{slide.quote}</p>
                    <div class="problem-source-chip problem-source-chip-in-quote">
                      <span class="problem-source-label">{sourceLabel}</span>
                      <a href={slide.sourceHref} target="_blank" rel="noreferrer" class="problem-source-link">
                        <span>{slide.source}</span>
                        <svg viewBox="0 0 24 24" class="problem-source-icon" aria-hidden="true">
                          <path
                            d="M14 5h5v5M10 14L19 5M19 13v4a2 2 0 0 1-2 2h-10a2 2 0 0 1-2-2v-10a2 2 0 0 1 2-2h4"
                            stroke-linecap="round"
                            stroke-linejoin="round"
                          ></path>
                        </svg>
                      </a>
                    </div>
                  </div>
                )}

                {!slide.hideSource && slide.source && slide.sourceHref && !slide.sourceInQuote && (
                  <div class="problem-source-chip-mobile slider-card-source-mobile">
                    <span class="problem-source-label">{sourceLabel}</span>
                    <a href={slide.sourceHref} target="_blank" rel="noreferrer" class="problem-source-link">
                      <span>{slide.source}</span>
                      <svg viewBox="0 0 24 24" class="problem-source-icon" aria-hidden="true">
                        <path
                          d="M14 5h5v5M10 14L19 5M19 13v4a2 2 0 0 1-2 2h-10a2 2 0 0 1-2-2v-10a2 2 0 0 1 2-2h4"
                          stroke-linecap="round"
                          stroke-linejoin="round"
                        ></path>
                      </svg>
                    </a>
                  </div>
                )}
              </article>
              );
            })
          }
        </div>
      </div>
    </div>
  </div>
  </div>
</section>

<style>
  /* Full-width section on all viewports */
  .problem-section {
    width: 100vw;
    max-width: 100vw;
    margin-left: calc(50% - 50vw);
    margin-right: calc(50% - 50vw);
    overflow-x: clip;
    box-sizing: border-box;
    padding-top: 0;
    padding-bottom: 0;
  }

  .problem-inner {
    width: 100%;
    max-width: 1360px;
    margin: 0 auto;
    padding: 0 20px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    overflow-x: clip;
  }

  .problem-section + .problem-section {
    margin-top: 80px;
  }

  #not-enough.problem-section {
    padding-bottom: 120px;
  }

  .problem-root {
    --accent-color: #fdb022;
    --title-line-w: 461.37px;
    --title-line-y: -56%;
    --title-line-w-md: 300px;
    --title-line-w-sm: 210px;
    --card-border-color: #b8804b;
    --card-bg-top: linear-gradient(280.32deg, #103037 -14.08%, #2e2317 110.46%);
    --card-bg-bottom: linear-gradient(
      260.34deg,
      rgba(255, 255, 255, 0.1) -0.52%,
      rgba(170, 157, 145, 0.1) 101.5%
    );
    --body-width: 560px;
    --body-left: 80px;
    --body-min-h: var(--body-h);
    --copy-width: 560px;
    --copy-head-gap: 16px;
    --kicker-color: #fff;
    --kicker-weight: 500;
    --kicker-size: 20px;
    --kicker-line-height: 24px;
    --kicker-size-md: 18px;
    --kicker-line-height-md: 22px;
    --kicker-transform: uppercase;
    --value-color: #fdb022;
    --value-size: 48px;
    --value-line-height: 58px;
    --highlight-color: #fdb022;
    --highlight-size: 28px;
    --highlight-line-height: 110%;
    --highlight-weight: 600;
    --description-line-height: 170%;
    --quote-bg: rgba(255, 255, 255, 0.1);
    --quote-border-color: rgba(255, 255, 255, 0.14);
    --quote-text-color: #fff;
    --quote-size: 16px;
    --quote-line-height: 150%;
    --source-chip-width: min(100%, var(--source-w));
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    gap: 32px;
    isolation: isolate;
    width: 100%;
    overflow-x: clip;
  }

  .problem-root.is-consequences {
    --accent-color: #f04438;
    --title-line-w: 226px;
    --title-line-y: -50%;
    --title-line-w-md: 208px;
    --title-line-w-sm: 160px;
    --card-bg-top: linear-gradient(-58.62deg, #1f170a 14.08%, #352121 110.46%);
    --card-bg-bottom: linear-gradient(
      240.31deg,
      rgba(255, 255, 255, 0.1) 0.52%,
      rgba(170, 157, 145, 0.1) 101.5%
    );
    --body-width: 600px;
    --body-left: 78px;
    --body-min-h: 252px;
    --copy-width: 600px;
    --copy-head-gap: 0;
    --kicker-color: #f04438;
    --kicker-weight: 600;
    --kicker-size: 36px;
    --kicker-line-height: 100%;
    --kicker-size-md: 32px;
    --kicker-line-height-md: 100%;
    --kicker-transform: none;
    --source-chip-width: fit-content;
  }

  .problem-root.is-world-solution {
    --accent-color: #4bb462;
    --title-line-w: 230px;
    --title-line-y: -48%;
    --title-line-w-md: 205px;
    --title-line-w-sm: 165px;
    --card-border-color: #4bb462;
    --card-bg-top: linear-gradient(280deg, #0f2518 -12%, #173b23 112%);
    --card-bg-bottom: linear-gradient(
      255deg,
      rgba(255, 255, 255, 0.12) -0.52%,
      rgba(88, 120, 95, 0.1) 101.5%
    );
    --body-width: 650px;
    --body-left: 72px;
    --body-min-h: 338px;
    --copy-width: 650px;
    --copy-head-gap: 6px;
    --kicker-color: #4bb462;
    --kicker-weight: 700;
    --kicker-size: 44px;
    --kicker-line-height: 110%;
    --kicker-size-md: 34px;
    --kicker-line-height-md: 110%;
    --kicker-transform: none;
    --highlight-color: #fdb022;
    --highlight-size: 32px;
    --highlight-line-height: 115%;
    --highlight-weight: 600;
    --description-line-height: 160%;
    --quote-bg: rgba(255, 255, 255, 0.08);
    --quote-border-color: rgba(255, 255, 255, 0.2);
    --quote-size: 20px;
    --quote-line-height: 160%;
    --source-chip-width: fit-content;
  }

  .problem-root.is-not-enough {
    --accent-color: #f79009;
    --title-line-w: 380px;
    --title-line-y: -50%;
    --title-line-w-md: 260px;
    --title-line-w-sm: 185px;
    --card-border-color: #d17254;
    --card-bg-top: linear-gradient(280deg, #2a1b14 -12%, #3b1f1d 112%);
    --card-bg-bottom: linear-gradient(
      255deg,
      rgba(255, 255, 255, 0.12) -0.52%,
      rgba(170, 157, 145, 0.1) 101.5%
    );
    --body-width: 560px;
    --body-left: 72px;
    --body-min-h: 248px;
    --copy-width: 560px;
    --copy-head-gap: 20px;
    --kicker-color: #fdb022;
    --kicker-weight: 700;
    --kicker-size: 36px;
    --kicker-line-height: 100%;
    --kicker-size-md: 34px;
    --kicker-line-height-md: 110%;
    --kicker-transform: none;
    --description-line-height: 24px;
  }

  .problem-root.is-not-enough .problem-body {
    gap: 32px;
  }

  .problem-root.is-not-enough .problem-layout {
    --card-h: 400px;
    --container-card-h: 500px;
    --container-extra: 280px;
  }

  .problem-root.is-not-enough .slider-container {
    overflow: visible;
  }

  .problem-title-wrap {
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 24px;
    width: 100%;
    min-height: 102px;
  }

  .problem-title {
    width: 100%;
    margin: 0;
    text-align: center;
    color: #fff;
    font-family: "Golos Text", sans-serif;
    font-style: normal;
    font-weight: 600;
    font-size: 48px;
    line-height: 58px;
  }

  .problem-accent {
    position: relative;
    display: inline-block;
    color: var(--accent-color);
  }

  .problem-title-line {
    position: absolute;
    width: var(--title-line-w);
    height: auto;
    left: 50%;
    top: 100%;
    transform: translate(-50%, var(--title-line-y));
    pointer-events: none;
  }

  .problem-title-subtitle {
    margin: 0;
    width: 100%;
    text-align: center;
    color: #fff;
    font-family: "Golos Text", sans-serif;
    font-style: normal;
    font-weight: 500;
    font-size: 16px;
    line-height: 20px;
    opacity: 0.95;
  }

  /* ── Slider layout ── */
  .slider-layout {
    --card-h: 460px;
    --container-card-h: var(--card-h);
    --container-extra: 80px;
    --stack-step: 20px;
    position: relative;
    display: flex;
    align-items: flex-start;
    justify-content: flex-end;
    width: 100%;
    overflow-x: clip;
  }

  .slider-title-wrap {
    position: relative;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 24px;
    width: 100%;
    min-height: 102px;
  }

  .slider-dots {
    position: absolute;
    left: 0;
    top: calc((var(--container-card-h) - 148px) / 2 + 20px);
    display: none;
    flex-direction: column;
    align-items: flex-start;
    gap: 8px;
    width: 8px;
    flex: none;
    z-index: 240;
  }

  .slider-dot {
    width: 8px;
    height: 20px;
    border: 0;
    border-radius: 8px;
    background: #fff;
    opacity: 0.2;
    cursor: pointer;
    transition: opacity 220ms ease, height 260ms ease;
    padding: 0;
  }

  .slider-dot[data-active="true"] {
    height: 36px;
    opacity: 1;
  }

  .slider-container {
    position: relative;
    width: min(1340px, calc(100% - 20px));
    height: auto;
    overflow: visible;
    flex: none;
    z-index: 1;
  }

  .slider-track {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    padding: 0;
    width: 100%;
    min-height: 100%;
    height: auto;
  }

  .problem-card {
    box-sizing: border-box;
    position: relative;
    width: 100%;
    height: var(--card-height, var(--card-h));
    border-radius: 52px;
    border: 2px solid var(--card-border-color);
    overflow: hidden;
    background: var(--card-bg-top), var(--card-bg-bottom);
    will-change: transform;
    isolation: isolate;
    scroll-snap-align: start;
  }

  .problem-card + .problem-card {
    margin-top: 20px;
  }

  .problem-math {
    position: absolute;
    inset: 0;
    background-image:
      repeating-linear-gradient(0deg, rgba(255, 255, 255, 0.2) 0 1px, transparent 1px 56px),
      repeating-linear-gradient(90deg, rgba(255, 255, 255, 0.2) 0 1px, transparent 1px 56px);
    background-size: 112px 112px;
    mix-blend-mode: hard-light;
    opacity: 0.1;
    transform: matrix(-1, 0, 0, 1, 0, 0);
    pointer-events: none;
  }

  .problem-effect {
    position: absolute;
    width: 276px;
    height: 276px;
    left: calc(50% - 138px + 390px);
    top: calc(50% - 138px + 42px);
    background: linear-gradient(180deg, #ce7f60 0%, #621321 100%);
    opacity: 0.5;
    filter: blur(100px);
    pointer-events: none;
  }

  .problem-image {
    position: absolute;
    width: 300px;
    height: 300px;
    right: 120px;
    top: 50%;
    transform: translateY(-50%);
    object-fit: contain;
    filter: drop-shadow(0 24px 44px rgba(0, 0, 0, 0.45));
  }

  .problem-root.is-consequences .problem-image {
    width: 276px;
    height: 276px;
    right: 132px;
  }

  .problem-side-image {
    position: absolute;
    display: none;
    width: 190px;
    height: auto;
    bottom: 42px;
    z-index: 3;
    pointer-events: none;
  }

  .problem-side-image.is-left {
    left: 52px;
  }

  .problem-side-image.is-right {
    right: 52px;
  }

  .problem-body {
    position: absolute;
    width: var(--body-width);
    min-height: var(--body-min-h);
    left: var(--body-left);
    top: 50%;
    transform: translateY(-50%);
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 24px;
  }

  .problem-copy-head {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: var(--copy-head-gap);
    width: var(--copy-width);
  }

  .problem-kicker {
    margin: 0;
    width: var(--copy-width);
    color: var(--kicker-color);
    font-family: "Golos Text", sans-serif;
    font-style: normal;
    font-weight: var(--kicker-weight);
    font-size: var(--kicker-size);
    line-height: var(--kicker-line-height);
    text-transform: var(--kicker-transform);
  }

  .problem-value {
    margin: 0;
    width: var(--copy-width);
    color: var(--value-color);
    font-family: "Golos Text", sans-serif;
    font-style: normal;
    font-weight: 700;
    font-size: var(--value-size);
    line-height: var(--value-line-height);
  }

  .problem-highlight {
    margin: 0;
    width: var(--copy-width);
    color: var(--highlight-color);
    font-family: "Golos Text", sans-serif;
    font-style: normal;
    font-weight: var(--highlight-weight);
    font-size: var(--highlight-size);
    line-height: var(--highlight-line-height);
  }

  .problem-copy-foot {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 24px;
    width: var(--copy-width);
  }

  .problem-description {
    margin: 0;
    width: var(--copy-width);
    color: #fff;
    font-family: "Golos Text", sans-serif;
    font-style: normal;
    font-weight: 400;
    font-size: var(--desc-size);
    line-height: var(--description-line-height);
    white-space: pre-line;
  }

  .problem-description-group {
    width: 100%;
    display: flex;
    flex-direction: column;
  }

  .problem-root.is-not-enough .problem-description-group {
    gap: 16px;
  }

  .problem-quote {
    margin: 0;
    width: var(--copy-width);
    border-radius: 16px;
    border: 1px solid var(--quote-border-color);
    background: var(--quote-bg);
    padding: 16px 20px;
    color: var(--quote-text-color);
    font-family: "Golos Text", sans-serif;
    font-style: normal;
    font-weight: 500;
    font-size: var(--quote-size);
    line-height: var(--quote-line-height);
    white-space: pre-line;
  }

  .problem-quote-box {
    width: var(--copy-width);
  }

  .problem-quote-box.is-source-card {
    border-radius: 16px;
    border: 1px solid var(--quote-border-color);
    background: var(--quote-bg);
    padding: 16px 20px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    gap: 14px;
  }

  .problem-quote-box.is-source-card .problem-quote {
    width: 100%;
    border: 0;
    border-radius: 0;
    background: transparent;
    padding: 0;
  }

  .problem-source-chip {
    display: inline-flex;
    flex-direction: row;
    align-items: center;
    gap: 6px;
    min-height: 52px;
    width: var(--source-chip-width);
    max-width: 100%;
    padding: 0 24px;
    border-radius: 16px;
    background: rgba(255, 255, 255, 0.1);
    box-sizing: border-box;
  }

  .problem-source-chip-mobile {
    display: none;
  }

  .problem-world-mobile-desc {
    display: none;
  }

  .problem-world-mobile-quote {
    margin: 0;
    color: #fff;
    font-family: "Golos Text", sans-serif;
    font-style: normal;
    font-weight: 500;
    font-size: 13px;
    line-height: 1.55;
    white-space: pre-line;
  }

  .problem-source-chip.is-hidden {
    display: none;
  }

  .problem-source-chip-in-quote {
    width: 100%;
    max-width: 100%;
    min-height: 0;
    padding: 0;
    border-radius: 0;
    background: transparent;
    gap: 8px;
  }

  .problem-source-chip-in-quote .problem-source-label {
    color: #a6abb4;
  }

  .problem-source-label {
    color: #85888e;
    font-family: "Golos Text", sans-serif;
    font-style: normal;
    font-weight: 500;
    font-size: 16px;
    line-height: 20px;
    white-space: nowrap;
  }

  .problem-source-link {
    display: inline-flex;
    align-items: center;
    gap: 6px;
    color: #4bb462;
    text-decoration: none;
    min-width: 0;
    font-family: "Golos Text", sans-serif;
    font-style: normal;
    font-weight: 500;
    font-size: 16px;
    line-height: 20px;
  }

  .problem-source-link span {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
  }

  .problem-source-link:hover {
    color: #82dea1;
  }

  .problem-source-icon {
    width: 20px;
    height: 20px;
    flex: none;
    fill: none;
    stroke: currentColor;
    stroke-width: 2;
  }

  .problem-root.is-world-solution .problem-effect {
    background: linear-gradient(180deg, #4bb462 0%, #1f5d31 100%);
  }

  .problem-root.is-world-solution .problem-image {
    width: auto;
    height: 280px;
    max-width: 420px;
    right: 76px;
    object-fit: contain;
    object-position: center;
  }

  .problem-root.is-world-solution .problem-kicker {
    color: #4bb462;
  }

  .problem-root.is-world-solution .problem-source-link,
  .problem-root.is-world-solution .problem-source-label {
    font-size: 14px;
    line-height: 18px;
  }

  .problem-root.is-world-solution .problem-source-icon {
    width: 18px;
    height: 18px;
  }

  .problem-card.is-not-enough-outro .problem-effect {
    left: 50%;
    right: auto;
    top: 64%;
    transform: translate(-50%, -50%);
    background: linear-gradient(180deg, #f04438 0%, #7f1d1d 100%);
    opacity: 0.45;
  }

  .problem-card.is-not-enough-outro .problem-body {
    position: absolute;
    inset: 176px 72px 236px;
    width: auto;
    min-height: auto;
    transform: none;
    align-items: center;
    text-align: center;
    gap: 16px;
  }

  .problem-card.is-not-enough-outro {
    --card-height: 620px;
    overflow: visible;
  }

  .problem-card.is-not-enough-outro .problem-copy-head,
  .problem-card.is-not-enough-outro .problem-copy-foot,
  .problem-card.is-not-enough-outro .problem-kicker,
  .problem-card.is-not-enough-outro .problem-description {
    width: 100%;
    text-align: center;
    align-items: center;
  }

  .problem-card.is-not-enough-outro .problem-copy-foot {
    gap: 0;
  }

  .problem-card.is-not-enough-outro .problem-kicker {
    color: #fdb022;
    font-size: 48px;
    line-height: 100%;
    font-weight: 600;
    text-transform: none;
  }

  .problem-card.is-not-enough-outro .problem-description {
    font-size: 20px;
    line-height: 1.8;
    letter-spacing: 0.3px;
    font-weight: 400;
  }

  .problem-card.is-not-enough-outro .problem-description-accent {
    color: #4bb462;
  }

  .problem-card.is-not-enough-outro .problem-image {
    width: 600px;
    height: auto;
    left: 50%;
    right: auto;
    top: 100%;
    bottom: auto;
    transform: translate(-50%, -56%);
    z-index: 5;
  }

  .problem-card.is-not-enough-outro .problem-side-image {
    display: block;
    width: 196px;
    top: 20px;
    bottom: auto;
    z-index: 8;
  }

  .problem-card.is-not-enough-outro .problem-side-image.is-left {
    display: block;
    left: 38px;
    right: auto;
    transform: none;
  }

  .problem-card.is-not-enough-outro .problem-side-image.is-right {
    right: 38px;
    left: auto;
    transform: none;
  }

  [data-slider-scroll-zone] {
    overscroll-behavior: contain;
  }

  /* Remove legacy list-mode overrides — mobile now uses flow mode */

  @media (min-width: 744px) and (max-width: 1366px) and (any-pointer: coarse) {
    .slider-layout {
      --card-h: 640px;
      --container-card-h: var(--card-h);
      --container-extra: 96px;
    }

    .problem-title {
      font-size: 40px;
      line-height: 48px;
    }

    .problem-title-line {
      width: var(--title-line-w-md);
      transform: translate(-50%, -48%);
    }

    .problem-card {
      border-radius: 34px;
    }

    .problem-effect {
      left: auto;
      right: 64px;
      top: calc(50% - 138px + 88px);
    }

    .problem-image {
      width: 320px;
      height: 320px;
      left: 50%;
      right: auto;
      top: auto;
      bottom: 24px;
      transform: translateX(-50%);
    }

    .problem-root.is-consequences .problem-image {
      width: 300px;
      height: 300px;
      left: 50%;
      right: auto;
      bottom: 20px;
      transform: translateX(-50%);
    }

    .problem-body {
      top: 32px;
      left: 32px;
      right: 32px;
      bottom: 336px;
      width: auto;
      min-height: auto;
      transform: none;
      gap: 20px;
    }

    .problem-copy-head,
    .problem-copy-foot,
    .problem-kicker,
    .problem-value,
    .problem-description {
      width: auto;
    }

    .problem-kicker {
      font-size: var(--kicker-size-md);
      line-height: var(--kicker-line-height-md);
    }

    .problem-value {
      font-size: 44px;
      line-height: 52px;
    }

    .problem-description {
      font-size: 16px;
    }

    .problem-highlight {
      font-size: 26px;
    }

    .problem-quote {
      font-size: 17px;
      padding: 14px 16px;
    }

    .problem-quote-box.is-source-card {
      padding: 14px 16px;
      gap: 12px;
    }

    .problem-root.is-world-solution .problem-image {
      width: auto;
      height: 300px;
      max-width: 430px;
      left: 50%;
      right: auto;
      bottom: 24px;
      transform: translateX(-50%);
    }

    .problem-root.is-world-solution .problem-layout {
      --card-h: 720px;
      --container-card-h: var(--card-h);
      --container-extra: 120px;
    }

    .problem-root.is-world-solution .problem-body {
      bottom: 360px;
    }

    .problem-root.is-not-enough .problem-body {
      bottom: 252px;
    }

    .problem-root.is-not-enough .problem-layout {
      --card-h: 560px;
      --container-card-h: 620px;
      --container-extra: 230px;
    }

    .problem-card.is-not-enough-outro .problem-body {
      inset: 156px 54px 208px;
      width: auto;
      transform: none;
      gap: 14px;
    }

    .problem-card.is-not-enough-outro .problem-kicker {
      font-size: 44px;
    }

    .problem-card.is-not-enough-outro .problem-description {
      font-size: 24px;
    }

    .problem-card.is-not-enough-outro .problem-image {
      width: 360px;
      left: 50%;
      right: auto;
      top: 100%;
      bottom: auto;
      transform: translate(-50%, -50%);
      z-index: 5;
    }

    .problem-card.is-not-enough-outro .problem-side-image {
      width: 156px;
      top: 18px;
      bottom: auto;
      z-index: 8;
    }

    .problem-card.is-not-enough-outro .problem-side-image.is-left {
      display: none;
    }

