---
import member01ImageUrl from "../assets/images/team/member-01.webp?url";
import member02ImageUrl from "../assets/images/team/member-02.webp?url";
import member03ImageUrl from "../assets/images/team/member-03.webp?url";
import member04ImageUrl from "../assets/images/team/member-04.webp?url";
import member05ImageUrl from "../assets/images/team/member-05.webp?url";
import member06ImageUrl from "../assets/images/team/member-06.webp?url";
import member07ImageUrl from "../assets/images/team/member-07.webp?url";
import member08ImageUrl from "../assets/images/team/member-08.webp?url";
import member09ImageUrl from "../assets/images/team/member-09.webp?url";
import member10ImageUrl from "../assets/images/team/member-10.webp?url";
import titleUnderlineUrl from "../assets/images/team/title-underline.svg?url";
import { teamMembers } from "../content/team";
import { DEFAULT_LANG, type Lang } from "../i18n/config";
import { getTranslations, resolveLang } from "../i18n/utils";

type Props = {
  lang?: Lang;
  sectionId?: string;
  sectionClassName?: string;
  headingLevel?: "h1" | "h2";
};

const {
  lang = DEFAULT_LANG,
  sectionId = "team",
  sectionClassName,
  headingLevel = "h1"
} = Astro.props as Props;

const activeLang = resolveLang(lang);
const content = getTranslations(activeLang).team;

const imageByKey: Record<string, string> = {
  "member-01": member01ImageUrl,
  "member-02": member02ImageUrl,
  "member-03": member03ImageUrl,
  "member-04": member04ImageUrl,
  "member-05": member05ImageUrl,
  "member-06": member06ImageUrl,
  "member-07": member07ImageUrl,
  "member-08": member08ImageUrl,
  "member-09": member09ImageUrl,
  "member-10": member10ImageUrl
};

const HeadingTag = headingLevel;
const railMembers = [...teamMembers, ...teamMembers];
---

<section id={sectionId} class:list={["team-section scroll-mt-28", sectionClassName]}>
  <header class="team-section__title-block">
    <HeadingTag class="team-section__title">{content.heading}</HeadingTag>
    <img
      src={titleUnderlineUrl}
      alt=""
      class="team-section__underline"
      width="321"
      height="4"
      loading="lazy"
      decoding="async"
    />
    <p class="team-section__subtitle">{content.subtitle}</p>
  </header>

  <ul class="team-section__grid">
    {
      teamMembers.map((member, index) => (
        <li id={member.slug} class="team-card">
          <div class="team-card__avatar-wrap">
            <img
              src={imageByKey[member.imageKey]}
              alt={`${member.name} — ${member.role[activeLang]}, ${member.project[activeLang]}`}
              class:list={["team-card__avatar", member.avatarClassName]}
              width="84"
              height="84"
              loading={index < 5 ? "eager" : "lazy"}
              decoding="async"
            />
          </div>
          <div class="team-card__copy">
            <p class="team-card__name">{member.name}</p>
            <p class="team-card__role">{member.role[activeLang]}</p>
          </div>
        </li>
      ))
    }
  </ul>

  <div class="team-section__rails" aria-hidden="true">
    <div class="team-section__rail team-section__rail--top">
      {
        railMembers.map((member, index) => (
          <article class="team-card team-card--rail">
            <div class="team-card__avatar-wrap">
              <img
                src={imageByKey[member.imageKey]}
                alt=""
                class:list={["team-card__avatar", member.avatarClassName]}
                width="84"
                height="84"
                loading="lazy"
                decoding="async"
              />
            </div>
            <div class="team-card__copy">
              <p class="team-card__name">{member.name}</p>
              <p class="team-card__role">{member.role[activeLang]}</p>
            </div>
          </article>
        ))
      }
    </div>

    <div class="team-section__rail team-section__rail--bottom">
      {
        railMembers.map((member, index) => (
          <article class="team-card team-card--rail">
            <div class="team-card__avatar-wrap">
              <img
                src={imageByKey[member.imageKey]}
                alt=""
                class:list={["team-card__avatar", member.avatarClassName]}
                width="84"
                height="84"
                loading="lazy"
                decoding="async"
              />
            </div>
            <div class="team-card__copy">
              <p class="team-card__name">{member.name}</p>
              <p class="team-card__role">{member.role[activeLang]}</p>
            </div>
          </article>
        ))
      }
    </div>
  </div>
</section>

<style>
  .team-section {
    padding: 40px 0 0;
    color: #fff;
  }

  .team-section__title-block {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 16px;
  }

  .team-section__title {
    margin: 0;
    font-family: "Golos Text", sans-serif;
    font-size: clamp(40px, 4vw, 48px);
    line-height: 1.04;
    font-weight: 600;
    text-align: center;
    color: #fff;
  }

  .team-section__underline {
    width: min(320px, calc(100vw - 52px));
    height: auto;
    margin-top: -10px;
    user-select: none;
  }

  .team-section__subtitle {
    margin: 0;
    font-family: "Golos Text", sans-serif;
    font-size: 16px;
    line-height: 20px;
    font-weight: 500;
    text-align: center;
    color: #fff;
  }

  .team-section__grid {
    margin: 32px 0 0;
    padding: 0;
    list-style: none;
    display: grid;
    gap: 12px;
    grid-template-columns: repeat(5, minmax(0, 1fr));
  }

  .team-section__rails {
    display: none;
  }

  .team-card {
    min-height: 200px;
    border-radius: 24px;
    background: rgba(255, 255, 255, 0.1);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 22px;
    padding: 14px 16px;
    box-shadow: inset 0 0 0 1px rgba(255, 255, 255, 0.03);
  }

  .team-card__avatar-wrap {
    width: 84px;
    height: 84px;
    border-radius: 999px;
    overflow: hidden;
    flex-shrink: 0;
  }

  .team-card__avatar {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    transform-origin: center;
    image-rendering: auto;
  }

  .team-card__avatar--offset-down {
    transform: scale(1.12);
    object-position: 50% 58%;
  }

  .team-card__copy {
    width: 100%;
    display: flex;
    flex-direction: column;
    gap: 8px;
    text-align: center;
  }

  .team-card__name {
    margin: 0;
    font-family: "Golos Text", sans-serif;
    font-size: 18px;
    line-height: 22px;
    font-weight: 500;
    color: #fff;
    letter-spacing: 0;
  }

  .team-card__role {
    margin: 0;
    font-family: "Golos Text", sans-serif;
    font-size: 14px;
    line-height: 18px;
    font-weight: 500;
    color: #4bb462;
    letter-spacing: 0;
  }

  @media (max-width: 1220px) {
    .team-section__grid {
      grid-template-columns: repeat(4, minmax(0, 1fr));
    }
  }

  @media (min-width: 744px) and (max-width: 1366px) and (any-pointer: coarse) {
    .team-section {
      padding-top: 32px;
    }

    .team-section__grid {
      grid-template-columns: repeat(3, minmax(0, 1fr));
    }
  }

  @media (max-width: 743px) {
    .team-section {
      margin-top: 80px;
      padding-top: 0;
    }

    .team-section__title {
      font-size: 36px;
      line-height: 43px;
    }

    .team-section__underline {
      width: 203px;
      margin-top: -8px;
    }

    .team-section__subtitle {
      font-size: 14px;
      line-height: 18px;
    }

    .team-section__grid {
      display: none;
    }

    .team-section__rails {
      margin-top: 32px;
      display: grid;
      gap: 8px;
      overflow: hidden;
      height: 392px;
    }

    .team-section__rail {
      display: flex;
      gap: 8px;
      width: max-content;
    }

    .team-section__rail--top {
      transform: translateX(-1155px);
    }

    .team-section__rail--bottom {
      transform: translateX(-1021px);
    }

    .team-card--rail {
      width: 260px;
      min-height: 180px;
      height: 180px;
      border-radius: 24px;
      padding: 13px 18px;
      justify-content: flex-start;
      gap: 22px;
    }

    .team-card--rail .team-card__avatar-wrap {
      margin: 0 auto;
    }

    .team-card--rail .team-card__copy {
      gap: 8px;
      text-align: center;
    }
  }

  @media (max-width: 540px) {
    .team-section__grid {
      grid-template-columns: 1fr;
    }

    .team-section__grid .team-card {
      min-height: 188px;
    }
  }
</style>

---
import chinaImageUrl from "../assets/images/world-solution/china.webp?url";
import europeImageUrl from "../assets/images/world-solution/europe.webp?url";
import londonImageUrl from "../assets/images/world-solution/london.webp?url";
import parisImageUrl from "../assets/images/world-solution/Paris.webp?url";
import titleLineImageUrl from "../assets/images/world-solution/line.svg?url";
import usaImageUrl from "../assets/images/world-solution/usa.webp?url";
import { DEFAULT_LANG, type Lang } from "../i18n/config";
import { getTranslations, resolveLang } from "../i18n/utils";
import StackedSliderSection, { type SlideItem } from "./StackedSliderSection.astro";

const slideImages = [usaImageUrl, parisImageUrl, chinaImageUrl, londonImageUrl, europeImageUrl];

type Props = {
  lang?: Lang;
  sectionId?: string;
  title?: string;
  titleAccent?: string;
  accentFirst?: boolean;
  subtitle?: string;
  slides?: SlideItem[];
  sliderAriaLabel?: string;
  dotsAriaLabel?: string;
  slideAriaSuffix?: string;
  sourceLabel?: string;
  sectionClassName?: string;
  variantClassName?: string;
};

const {
  lang = DEFAULT_LANG,
  sectionId = "countries",
  title,
  titleAccent,
  accentFirst,
  subtitle,
  slides,
  sliderAriaLabel,
  dotsAriaLabel,
  slideAriaSuffix,
  sourceLabel,
  sectionClassName,
  variantClassName = "is-world-solution"
} = Astro.props as Props;

const activeLang = resolveLang(lang);
const content = getTranslations(activeLang).home.sections.worldSolution;

const resolvedSlides: SlideItem[] =
  slides ??
  content.slides.map((slide, index) => ({
    ...slide,
    imageUrl: slideImages[index] ?? slideImages[0]
  }));
---

<StackedSliderSection
  sectionId={sectionId}
  title={title ?? content.title}
  titleAccent={titleAccent ?? content.titleAccent}
  accentFirst={accentFirst ?? content.accentFirst}
  subtitle={subtitle ?? content.subtitle}
  slides={resolvedSlides}
  sectionClassName={sectionClassName}
  variantClassName={variantClassName}
  titleLineImageUrl={titleLineImageUrl}
  sliderAriaLabel={sliderAriaLabel ?? content.sliderAriaLabel}
  dotsAriaLabel={dotsAriaLabel ?? content.dotsAriaLabel}
  slideAriaSuffix={slideAriaSuffix ?? content.slideAriaSuffix}
  sourceLabel={sourceLabel ?? content.sourceLabel}
/>

layouts.content:
---
import "../styles/global.css";
import CursorTrail from "../components/CursorTrail.astro";
import SiteHeader from "../components/SiteHeader.astro";
import SiteFooter from "../components/SiteFooter.astro";
import { DEFAULT_LANG, LANGUAGES, type Lang } from "../i18n/config";
import { getTranslations, localizedPath, resolveLang } from "../i18n/utils";
import { withBase } from "../utils/url";

type Props = {
  title?: string;
  description?: string;
  keywords?: string;
  ogImage?: string;
  lang?: Lang;
  routePath?: string;
  theme?: "light" | "landing";
};

const {
  title,
  description,
  keywords,
  ogImage = "/og-image.jpg",
  lang = DEFAULT_LANG,
  routePath = "/",
  theme = "light"
} = Astro.props;

const activeLang: Lang = resolveLang(lang);
const t = getTranslations(activeLang);
const pageTitle = title ?? t.layout.defaultTitle;
const pageDescription = description ?? t.layout.defaultDescription;
const pageKeywords = typeof keywords === "string" ? keywords.trim() : undefined;
const isLandingTheme = theme === "landing";
const ogImagePath =
  /^https?:\/\//i.test(ogImage) ? ogImage : withBase(ogImage);
const ogImageUrl = new URL(ogImagePath, Astro.url).toString();
const canonicalUrl = Astro.url.toString();
const baseUrl = Astro.site ?? Astro.url;
const siteUrl = baseUrl.toString().replace(/\/$/, "");
const alternateLinks = LANGUAGES.map((locale) => ({
  locale,
  href: new URL(localizedPath(locale, routePath), baseUrl).toString()
}));
const xDefaultUrl = new URL(localizedPath(DEFAULT_LANG, routePath), baseUrl).toString();
const logoUrl = new URL(withBase("/logo.png"), baseUrl).toString();

const ogLocaleMap: Record<Lang, string> = {
  uz: "uz_UZ",
  ru: "ru_RU",
  en: "en_US"
};
const ogLocale = ogLocaleMap[activeLang];
const ogLocaleAlternates = LANGUAGES
  .filter((locale) => locale !== activeLang)
  .map((locale) => ogLocaleMap[locale]);

const schemaObjects: Record<string, unknown>[] = [
  {
    "@context": "https://schema.org",
    "@type": "Organization",
    name: t.layout.siteName,
    url: siteUrl,
    logo: logoUrl,
    contactPoint: [
      {
        "@type": "ContactPoint",
        contactType: "customer support",
        telephone: t.layout.footer.phone,
        email: t.layout.footer.email
      }
    ]
  },
  {
    "@context": "https://schema.org",
    "@type": "WebSite",
    name: t.layout.siteName,
    url: siteUrl,
    inLanguage: activeLang
  },
  {
    "@context": "https://schema.org",
    "@type": "WebPage",
    name: pageTitle,
    description: pageDescription,
    url: canonicalUrl,
    inLanguage: activeLang
  }
];

if (routePath === "/") {
  schemaObjects.push({
    "@context": "https://schema.org",
    "@type": "SoftwareApplication",
    name: t.layout.siteName,
    applicationCategory: "EducationApplication",
    operatingSystem: "Android, iOS",
    inLanguage: activeLang,
    url: canonicalUrl,
    description: pageDescription,
    image: ogImageUrl,
    publisher: {
      "@type": "Organization",
      name: t.layout.siteName,
      url: siteUrl
    }
  });
}
---

<!doctype html>
<html lang={activeLang}>
  <head>
    <meta charset="utf-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1" />
    <title>{pageTitle}</title>
    <meta name="description" content={pageDescription} />
    {pageKeywords && <meta name="keywords" content={pageKeywords} />}
    <meta
      name="robots"
      content="index, follow, max-image-preview:large, max-snippet:-1, max-video-preview:-1"
    />
    <meta name="theme-color" content={isLandingTheme ? "#090b10" : "#ffffff"} />
    <link rel="canonical" href={canonicalUrl} />
    {
      alternateLinks.map(({ locale, href }) => (
        <link rel="alternate" hreflang={locale} href={href} />
      ))
    }
    <link rel="alternate" hreflang="x-default" href={xDefaultUrl} />
    <link rel="icon" href={withBase("/favicon.ico")} sizes="any" />
    <link rel="icon" type="image/png" sizes="32x32" href={withBase("/favicon-32x32.png")} />
    <link rel="icon" type="image/png" sizes="16x16" href={withBase("/favicon-16x16.png")} />
    <link rel="apple-touch-icon" sizes="180x180" href={withBase("/apple-touch-icon.png")} />
    <link rel="manifest" href={withBase("/site.webmanifest")} />

    <meta property="og:type" content="website" />
    <meta property="og:site_name" content={t.layout.siteName} />
    <meta property="og:locale" content={ogLocale} />
    {
      ogLocaleAlternates.map((locale) => (
        <meta property="og:locale:alternate" content={locale} />
      ))
    }
    <meta property="og:title" content={pageTitle} />
    <meta property="og:description" content={pageDescription} />
    <meta property="og:url" content={canonicalUrl} />
    <meta property="og:image" content={ogImageUrl} />
    <meta property="og:image:secure_url" content={ogImageUrl} />
    <meta property="og:image:type" content="image/jpeg" />
    <meta property="og:image:width" content="1200" />
    <meta property="og:image:height" content="630" />
    <meta property="og:image:alt" content={pageTitle} />

    <meta name="twitter:card" content="summary_large_image" />
    <meta name="twitter:title" content={pageTitle} />
    <meta name="twitter:description" content={pageDescription} />
    <meta name="twitter:image" content={ogImageUrl} />
    <meta name="twitter:image:alt" content={pageTitle} />

    <link rel="preconnect" href="https://fonts.googleapis.com" />
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin />
    <link
      href="https://fonts.googleapis.com/css2?family=Golos+Text:wght@400;500;600;700;800;900&display=swap"
      rel="stylesheet"
    />
    {
      schemaObjects.map((schemaObject) => (
        <script type="application/ld+json" is:inline set:html={JSON.stringify(schemaObject)} />
      ))
    }

    <slot name="head" />
  </head>
  <body
    class:list={[
      "font-sans text-ink",
      isLandingTheme ? "relative bg-[#090b10]" : ""
    ]}
  >
    {
      isLandingTheme && (
        <>
          <div
            aria-hidden="true"
            class="pointer-events-none fixed inset-0 -z-10"
            style="background-image: radial-gradient(920px 920px at -220px -220px, rgba(226, 118, 28, 0.28) 0%, rgba(226, 118, 28, 0) 68%), radial-gradient(900px 900px at calc(100% + 220px) -140px, rgba(35, 159, 193, 0.24) 0%, rgba(35, 159, 193, 0) 66%), linear-gradient(180deg, #080a10 0%, #090b10 45%, #0b0d12 100%);"
          >
          </div>
          <div
            aria-hidden="true"
            class="pointer-events-none fixed inset-0 -z-10 opacity-[0.08] mix-blend-soft-light"
            style="background-image: linear-gradient(rgba(255,255,255,0.06) 1px, transparent 1px), linear-gradient(90deg, rgba(255,255,255,0.06) 1px, transparent 1px); background-size: 112px 112px;"
          >
          </div>
        </>
      )
    }

    <SiteHeader lang={activeLang} routePath={routePath} />
    <CursorTrail />

    <main class="mx-auto w-full max-w-[1360px] px-5 lg:px-6">
      <slot />
    </main>

    <div class="mx-auto w-full max-w-[1360px] px-5 lg:px-6">
      <SiteFooter lang={activeLang} />
    </div>

    <script is:inline>
      (() => {
        if (window.__tikonchaVideoModalBooted) return;
        window.__tikonchaVideoModalBooted = true;

        const getModal = () => document.querySelector("[data-video-modal]");
        const getPlayer = () => document.querySelector("[data-video-modal-player]");

        const closeModal = () => {
          const modal = getModal();
          const player = getPlayer();
          if (!modal || !player) return;
          player.pause();
          player.removeAttribute("src");
          player.load();
          modal.hidden = true;
          modal.setAttribute("aria-hidden", "true");
          document.body.classList.remove("video-modal-open");
        };

        const openModal = (src) => {
          const modal = getModal();
          const player = getPlayer();
          if (!modal || !player || !src) return;
          modal.hidden = false;
          modal.setAttribute("aria-hidden", "false");
          document.body.classList.add("video-modal-open");
          if (player.getAttribute("src") !== src) {
            player.setAttribute("src", src);
            player.load();
          }
          const playPromise = player.play();
          if (playPromise && typeof playPromise.catch === "function") {
            playPromise.catch(() => {});
          }
        };

        document.addEventListener("click", (event) => {
          const target = event.target;
          if (!(target instanceof Element)) return;

          const trigger = target.closest("[data-video-modal-trigger]");
          if (trigger) {
            event.preventDefault();
            const source = trigger.getAttribute("data-video-src") || trigger.getAttribute("href") || "";
            openModal(source);
            return;
          }

          if (target.closest("[data-video-modal-close]")) {
            event.preventDefault();
            closeModal();
          }
        });

        window.addEventListener("keydown", (event) => {
          if (event.key === "Escape") {
            closeModal();
          }
        });

        document.addEventListener("astro:before-swap", closeModal);
      })();
    </script>
  </body>
</html>

styles.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
    /* Typography tokens */
    --font-brand: "Golos Text";
    --font-sans: var(--font-brand), ui-sans-serif, system-ui, sans-serif;

    /* Color tokens (RGB triples for Tailwind opacity support) */
    --color-ink: 11 12 16;
    --color-mist: 247 244 239;
    --color-clay: 215 197 176;
    --color-tide: 18 93 93;
    --color-primary: 11 13 12;
    --color-secondary: 75 180 98;
    --color-cursor-trail: var(--color-secondary);
    --color-page-bg: var(--color-mist);

    color-scheme: dark light;
}

html {
    scroll-behavior: smooth;
    scrollbar-width: none;
    -ms-overflow-style: none;
}

body {
    font-family: var(--font-sans);
    background-color: rgb(var(--color-page-bg));
    scrollbar-width: none;
    -ms-overflow-style: none;
}

html::-webkit-scrollbar,
body::-webkit-scrollbar {
    width: 0;
    height: 0;
    display: none;
}

::selection {
    background: rgb(var(--color-tide));
    color: white;
}

scripts.content:
type Point = {
  x: number;
  y: number;
};

const prefersReducedMotion = window.matchMedia("(prefers-reduced-motion: reduce)").matches;
const hasFinePointer = window.matchMedia("(pointer: fine)").matches;

if (!prefersReducedMotion && hasFinePointer) {
  const canvas = document.getElementById("cursor-trail-canvas");

  if (canvas instanceof HTMLCanvasElement && !canvas.dataset.ready) {
    canvas.dataset.ready = "true";

    const context = canvas.getContext("2d");
    if (!context) {
      // Canvas context unavailable, skip effect.
    } else {
      const MIN_TRAIL_LENGTH_PX = 10;
      const MAX_TRAIL_LENGTH_PX = 100;
      const SPEED_MIN_PX_PER_MS = 0.18;
      const SPEED_MAX_PX_PER_MS = 2.1;
      const BASE_HEAD_RADIUS = 6.2;
      const MAX_HEAD_RADIUS = 9.5;
      const IDLE_SHRINK_PX = 13;
      const IDLE_THRESHOLD_MS = 72;

      const points: Point[] = [];
      let dpr = 1;
      let pointerVisible = false;
      let pointerX = 0;
      let pointerY = 0;
      let lastMoveTime = 0;
      let lastEventTime = 0;
      let smoothedSpeed = 0;

      const clamp = (value: number, min: number, max: number): number =>
        Math.max(min, Math.min(max, value));

      const toCanvasColor = (raw: string): string => {
        if (!raw) return "";
        if (raw.startsWith("rgb(") || raw.startsWith("rgba(") || raw.startsWith("#")) return raw;

        const parts = raw.split(/\s+/).filter(Boolean).slice(0, 3);
        const isRgbTriple = parts.length === 3 && parts.every((part) => !Number.isNaN(Number(part)));
        return isRgbTriple ? `rgb(${parts.join(",")})` : "";
      };

      const getTrailColor = (): string => {
        const styles = getComputedStyle(document.documentElement);
        const candidates = [
          styles.getPropertyValue("--color-cursor-trail").trim(),
          styles.getPropertyValue("--color-secondary").trim()
        ];

        for (const candidate of candidates) {
          const parsed = toCanvasColor(candidate);
          if (parsed) return parsed;
        }

        return "rgb(75,180,98)";
      };

      let trailColor = getTrailColor();

      const distance = (a: Point, b: Point): number => Math.hypot(b.x - a.x, b.y - a.y);

      const getSpeedProgress = (): number => {
        const normalized = clamp(
          (smoothedSpeed - SPEED_MIN_PX_PER_MS) / (SPEED_MAX_PX_PER_MS - SPEED_MIN_PX_PER_MS),
          0,
          1
        );
        return normalized ** 1.85;
      };

      const getDynamicTrailLength = (): number =>
        MIN_TRAIL_LENGTH_PX + (MAX_TRAIL_LENGTH_PX - MIN_TRAIL_LENGTH_PX) * getSpeedProgress();

      const getDynamicHeadRadius = (): number =>
        BASE_HEAD_RADIUS + (MAX_HEAD_RADIUS - BASE_HEAD_RADIUS) * getSpeedProgress();

      const resizeCanvas = (): void => {
        dpr = Math.max(window.devicePixelRatio || 1, 1);
        const width = window.innerWidth;
        const height = window.innerHeight;

        canvas.width = Math.round(width * dpr);
        canvas.height = Math.round(height * dpr);
        canvas.style.width = `${width}px`;
        canvas.style.height = `${height}px`;
        context.setTransform(dpr, 0, 0, dpr, 0, 0);
        trailColor = getTrailColor();

        if (!pointerVisible) {
          pointerX = width * 0.5;
          pointerY = height * 0.5;
        }
      };

      const clampTrailLength = (maxLength: number): void => {
        let totalLength = 0;
        for (let i = 1; i < points.length; i += 1) {
          totalLength += distance(points[i - 1], points[i]);
        }

        while (totalLength > maxLength && points.length > 1) {
          const first = points[0];
          const second = points[1];
          const segmentLength = distance(first, second) || 0.0001;
          const overflow = totalLength - maxLength;

          if (segmentLength <= overflow) {
            points.shift();
            totalLength -= segmentLength;
          } else {
            const ratio = overflow / segmentLength;
            first.x += (second.x - first.x) * ratio;
            first.y += (second.y - first.y) * ratio;
            totalLength = maxLength;
          }
        }
      };

      const shrinkTrail = (shrinkPx: number): void => {
        let remaining = shrinkPx;
        while (remaining > 0 && points.length > 1) {
          const first = points[0];
          const second = points[1];
          const segmentLength = distance(first, second) || 0.0001;

          if (segmentLength <= remaining) {
            points.shift();
            remaining -= segmentLength;
          } else {
            const ratio = remaining / segmentLength;
            first.x += (second.x - first.x) * ratio;
            first.y += (second.y - first.y) * ratio;
            remaining = 0;
          }
        }
      };

      const drawTrailStroke = (lineWidth: number): void => {
        if (points.length < 2) return;

        context.save();
        context.strokeStyle = trailColor;
        context.globalAlpha = 0.92;
        context.lineWidth = lineWidth;
        context.lineCap = "round";
        context.lineJoin = "round";
        context.beginPath();
        context.moveTo(points[0].x, points[0].y);

        for (let i = 1; i < points.length - 1; i += 1) {
          const next = points[i + 1];
          const midpointX = (points[i].x + next.x) * 0.5;
          const midpointY = (points[i].y + next.y) * 0.5;
          context.quadraticCurveTo(points[i].x, points[i].y, midpointX, midpointY);
        }

        const tail = points[points.length - 1];
        context.lineTo(tail.x, tail.y);
        context.stroke();
        context.restore();
      };

      const drawHeadCircle = (radius: number): void => {
        if (!pointerVisible) return;
        context.save();
        context.fillStyle = trailColor;
        context.globalAlpha = 0.98;
        context.beginPath();
        context.arc(pointerX, pointerY, radius, 0, Math.PI * 2);
        context.fill();
        context.restore();
      };

      const onPointerMove = (event: PointerEvent): void => {
        const now = performance.now();
        const { clientX, clientY } = event;
        const deltaX = clientX - pointerX;
        const deltaY = clientY - pointerY;
        const deltaTime = lastEventTime > 0 ? Math.max(now - lastEventTime, 1) : 16;
        const instantSpeed = Math.hypot(deltaX, deltaY) / deltaTime;
        smoothedSpeed = smoothedSpeed * 0.82 + instantSpeed * 0.18;

        pointerX = clientX;
        pointerY = clientY;
        pointerVisible = true;
        lastMoveTime = now;
        lastEventTime = now;

        const lastPoint = points[points.length - 1];
        if (!lastPoint || Math.hypot(pointerX - lastPoint.x, pointerY - lastPoint.y) > 1.2) {
          points.push({ x: pointerX, y: pointerY });
        }

        clampTrailLength(getDynamicTrailLength());
      };

      const render = (time: number): void => {
        const idleMs = time - lastMoveTime;
        const isMoving = idleMs < IDLE_THRESHOLD_MS;
        const width = window.innerWidth;
        const height = window.innerHeight;

        context.clearRect(0, 0, width, height);

        if (pointerVisible) {
          const trailLength = getDynamicTrailLength();
          const headRadius = getDynamicHeadRadius();
          const lineWidth = headRadius * 2;

          if (isMoving) {
            clampTrailLength(trailLength);
          } else {
            smoothedSpeed *= 0.76;
            shrinkTrail(IDLE_SHRINK_PX);
          }

          drawTrailStroke(lineWidth);
          drawHeadCircle(headRadius);

          if (!isMoving && points.length < 2 && smoothedSpeed < 0.0015) {
            points.length = 0;
          }
        }

        requestAnimationFrame(render);
      };

      resizeCanvas();
      window.addEventListener("resize", resizeCanvas);
      window.addEventListener("pointermove", onPointerMove, { passive: true });
      window.addEventListener(
        "pointerdown",
        (event) => {
          if (event instanceof PointerEvent) onPointerMove(event);
        },
        { passive: true }
      );
      window.addEventListener(
        "pointerleave",
        () => {
          pointerVisible = false;
          points.length = 0;
        },
        { passive: true }
      );

      requestAnimationFrame(render);
    }
  }
}

const howWorkSections = document.querySelectorAll<HTMLElement>("[data-how-tabs]");

const MAIN_DURATION = 1000;
const TAB_DURATION = 760;
const STEP_DURATION = 760;
const EASE = "cubic-bezier(0.77, 0, 0.175, 1)";
const LOCK_RESET_VISIBILITY_RATIO = 0.02;
const DESKTOP_LOCK_TRIGGER_TOP_TOLERANCE_PX = 12;
const MOBILE_LOCK_TRIGGER_TOP_TOLERANCE_PX = 24;
const FAST_SKIP_DELTA_THRESHOLD = 220;
const SOFT_SNAP_DISTANCE_PX = 96;
const SOFT_SNAP_DELTA_THRESHOLD = 120;
const WHEEL_DELTA_LOCK_THRESHOLD = 50;
const TOUCH_STEP_LOCK_THRESHOLD = 28;
const LOCK_EXIT_NUDGE_PX = 8;
const WHEEL_STEP_COOLDOWN_MS = 700;

howWorkSections.forEach((section) => {
  if (section.dataset.ready === "true") return;
  section.dataset.ready = "true";

  const hostSection = (section.closest(".how-work-section") as HTMLElement | null) ?? section;
  const tabs = Array.from(section.querySelectorAll<HTMLButtonElement>("[data-how-tab]"));
  const panels = Array.from(section.querySelectorAll<HTMLElement>("[data-how-panel]"));
  const panelsWrap = section.querySelector<HTMLElement>("[data-how-panels-wrap]");
  const gifs = Array.from(section.querySelectorAll<HTMLImageElement>("[data-how-gif]"));
  const stepLabel = section.querySelector<HTMLElement>("[data-how-step]");
  const siteHeader = document.querySelector<HTMLElement>("[data-site-header]");

  if (tabs.length === 0 || panels.length === 0 || gifs.length === 0) return;

  const lastIndex = Math.max(0, tabs.length - 1);
  const clamp = (value: number): number => Math.min(lastIndex, Math.max(0, value));
  const clampRange = (value: number, min: number, max: number): number => Math.min(max, Math.max(min, value));

  const parsedIndex = Number.parseInt(section.dataset.activeIndex ?? "0", 10);
  let activeIndex = Number.isFinite(parsedIndex) ? clamp(parsedIndex) : 0;

  let isTransitioning = false;
  let wheelLocked = false;
  let isSectionLocked = false;
  let lastReleaseDirection: 1 | -1 | 0 = 0;
  let lockAnchorTop = 0;
  let previousScrollY = window.scrollY;
  let isPageScrollFrozen = false;
  let savedHtmlOverflowY = "";
  let savedBodyOverflowY = "";
  let savedBodyOverscrollY = "";
  let wheelDeltaAccumulator = 0;
  let wheelDirectionAccumulator: 1 | -1 | 0 = 0;
  let touchStartY: number | null = null;
  let touchCurrentY: number | null = null;
  let touchDeltaAccumulator = 0;
  let touchDirectionAccumulator: 1 | -1 | 0 = 0;
  let queuedTabTarget: number | null = null;
  let queuedTabFocus = false;
  const isMobileLayout = (): boolean => window.matchMedia("(max-width: 743px)").matches;
  const isStaticLayoutMode = (): boolean =>
    window.matchMedia(
      "(max-width: 743px), (min-width: 744px) and (max-width: 1366px) and (any-pointer: coarse)"
    ).matches;
  const getLockTriggerTopTolerance = (): number =>
    isMobileLayout() ? MOBILE_LOCK_TRIGGER_TOP_TOLERANCE_PX : DESKTOP_LOCK_TRIGGER_TOP_TOLERANCE_PX;

  const getViewportBounds = (): { top: number; bottom: number; height: number } => {
    const viewportHeight = window.innerHeight || document.documentElement.clientHeight;
    const headerHeight = siteHeader ? Math.round(siteHeader.getBoundingClientRect().height) : 0;
    const top = Math.max(0, headerHeight);
    const bottom = Math.max(top + 1, viewportHeight);
    return {
      top,
      bottom,
      height: bottom - top
    };
  };

  const clearInlineFx = (element: HTMLElement | HTMLImageElement): void => {
    element.style.removeProperty("opacity");
    element.style.removeProperty("transform");
    element.style.removeProperty("filter");
  };

  const measurePanelHeight = (panel: HTMLElement): number => {
    const measured = Math.max(panel.scrollHeight, Math.ceil(panel.getBoundingClientRect().height));
    return measured > 0 ? measured : 0;
  };

  const syncPanelsHeight = (): void => {
    if (!panelsWrap) return;
    if (isStaticLayoutMode()) {
      panelsWrap.style.removeProperty("height");
      return;
    }
    const current = panels[activeIndex];
    if (!current) return;

    const currentHeight = measurePanelHeight(current);
    if (currentHeight > 0) panelsWrap.style.height = `${currentHeight}px`;
  };

  const updateA11yAndActiveTab = (): void => {
    tabs.forEach((tab, index) => {
      const isActive = index === activeIndex;
      tab.dataset.active = isActive ? "true" : "false";
      tab.setAttribute("aria-selected", isActive ? "true" : "false");
      tab.tabIndex = isActive ? 0 : -1;
    });
  };

  const getSectionAnchorTop = (): number => {
    const viewportBounds = getViewportBounds();
    const sectionTop = hostSection.getBoundingClientRect().top + window.scrollY;
    return Math.max(0, Math.round(sectionTop - viewportBounds.top));
  };

  const getSectionLockRange = (): { start: number; end: number } => {
    const start = getSectionAnchorTop();
    const sectionHeight = Math.max(1, Math.round(hostSection.getBoundingClientRect().height));
    return {
      start,
      end: start + sectionHeight
    };
  };

  const getSoftSnapAnchor = (currentY: number, delta: number): number => {
    const { start, end } = getSectionLockRange();
    if (delta > SOFT_SNAP_DELTA_THRESHOLD) return currentY;

    const distanceToStart = Math.abs(currentY - start);
    const distanceToEnd = Math.abs(currentY - end);

    if (distanceToStart <= SOFT_SNAP_DISTANCE_PX) return start;
    if (distanceToEnd <= SOFT_SNAP_DISTANCE_PX) return end;
    return currentY;
  };

  const freezePageScroll = (): void => {
    if (isPageScrollFrozen) return;
    const html = document.documentElement;
    const body = document.body;

    savedHtmlOverflowY = html.style.overflowY;
    savedBodyOverflowY = body.style.overflowY;
    savedBodyOverscrollY = body.style.overscrollBehaviorY;

    html.style.overflowY = "hidden";
    body.style.overflowY = "hidden";
    body.style.overscrollBehaviorY = "none";
    isPageScrollFrozen = true;
  };

  const unfreezePageScroll = (): void => {
    if (!isPageScrollFrozen) return;
    const html = document.documentElement;
    const body = document.body;

    html.style.overflowY = savedHtmlOverflowY;
    body.style.overflowY = savedBodyOverflowY;
    body.style.overscrollBehaviorY = savedBodyOverscrollY;
    isPageScrollFrozen = false;
  };

  const releaseSectionLock = (direction: 1 | -1): void => {
    const { start, end } = getSectionLockRange();
    isSectionLocked = false;
    unfreezePageScroll();
    lastReleaseDirection = direction;
    wheelLocked = false;
    wheelDeltaAccumulator = 0;
    wheelDirectionAccumulator = 0;
    touchDeltaAccumulator = 0;
    touchDirectionAccumulator = 0;

    window.scrollTo({
      top: direction > 0 ? end + LOCK_EXIT_NUDGE_PX : Math.max(0, start - LOCK_EXIT_NUDGE_PX),
      behavior: "auto"
    });
  };

  const engageSectionLock = (anchorTop: number): void => {
    const { start, end } = getSectionLockRange();
    lockAnchorTop = clampRange(anchorTop, start, end);
    isSectionLocked = true;
    freezePageScroll();
    lastReleaseDirection = 0;
    wheelDeltaAccumulator = 0;
    wheelDirectionAccumulator = 0;
    touchDeltaAccumulator = 0;
    touchDirectionAccumulator = 0;

    if (Math.abs(window.scrollY - lockAnchorTop) > 1) {
      window.scrollTo({ top: lockAnchorTop, behavior: "auto" });
    }
  };

  const getSectionVisibilityRatio = (): number => {
    const rect = hostSection.getBoundingClientRect();
    const viewportBounds = getViewportBounds();
    const visible = Math.max(
      0,
      Math.min(rect.bottom, viewportBounds.bottom) - Math.max(rect.top, viewportBounds.top)
    );
    const targetHeight = Math.max(1, Math.min(rect.height, viewportBounds.height));
    return visible / targetHeight;
  };

  const isSectionTopAlignedWithViewport = (): boolean => {
    const rect = hostSection.getBoundingClientRect();
    const viewportBounds = getViewportBounds();
    const tolerance = getLockTriggerTopTolerance();
    return Math.abs(rect.top - viewportBounds.top) <= tolerance;
  };

  const verticalSwap = (
    outgoing: HTMLElement | HTMLImageElement,
    incoming: HTMLElement | HTMLImageElement,
    direction: 1 | -1
  ): Promise<void> => {
    const canAnimate = typeof outgoing.animate === "function" && typeof incoming.animate === "function";

    const enterFrom = direction === 1 ? 100 : -100;
    const exitTo = direction === 1 ? -100 : 100;

    incoming.classList.add("is-active");

    if (!canAnimate) {
      outgoing.classList.remove("is-active");
      clearInlineFx(outgoing);
      clearInlineFx(incoming);
      return Promise.resolve();
    }

    const outAnim = outgoing.animate(
      [
        {
          opacity: 1,
          transform: "translate3d(0, 0, 0)",
          filter: "blur(0px)"
        },
        {
          opacity: 0,
          transform: `translate3d(0, ${exitTo}%, 0)`,
          filter: "blur(4px)"
        }
      ],
      {
        duration: MAIN_DURATION,
        easing: EASE,
        fill: "forwards"
      }
    );

    const inAnim = incoming.animate(
      [
        {
          opacity: 0,
          transform: `translate3d(0, ${enterFrom}%, 0)`,
          filter: "blur(4px)"
        },
        {
          opacity: 1,
          transform: "translate3d(0, 0, 0)",
          filter: "blur(0px)"
        }
      ],
      {
        duration: MAIN_DURATION,
        easing: EASE,
        fill: "forwards"
      }
    );

    return Promise.allSettled([outAnim.finished, inAnim.finished]).then(() => {
      outgoing.classList.remove("is-active");
      clearInlineFx(outgoing);
      clearInlineFx(incoming);
    });
  };

  const animateTabs = (
    outgoingTab: HTMLButtonElement,
    incomingTab: HTMLButtonElement,
    _direction: 1 | -1
  ): Promise<void> => {
    const canAnimate = typeof outgoingTab.animate === "function" && typeof incomingTab.animate === "function";
    if (!canAnimate) return Promise.resolve();

    const outAnim = outgoingTab.animate(
      [{ opacity: 1 }, { opacity: 0.42 }],
      {
        duration: Math.min(360, TAB_DURATION),
        easing: EASE,
        fill: "forwards"
      }
    );

    const inAnim = incomingTab.animate(
      [{ opacity: 0.42 }, { opacity: 1 }],
      {
        duration: Math.min(360, TAB_DURATION),
        easing: EASE,
        fill: "forwards"
      }
    );

    return Promise.allSettled([outAnim.finished, inAnim.finished]).then(() => {
      outgoingTab.style.removeProperty("opacity");
      outgoingTab.style.removeProperty("transform");
      incomingTab.style.removeProperty("opacity");
      incomingTab.style.removeProperty("transform");
    });
  };

  const animateStep = (direction: 1 | -1, nextStep: string): Promise<void> => {
    if (!stepLabel || typeof stepLabel.animate !== "function") {
      if (stepLabel) stepLabel.textContent = nextStep;
      return Promise.resolve();
    }

    const parent = stepLabel.parentElement;
    if (!parent) {
      stepLabel.textContent = nextStep;
      return Promise.resolve();
    }

    const outgoingStep = stepLabel.textContent ?? "";
    const outTo = direction === 1 ? -110 : 110;
    const inFrom = direction === 1 ? 110 : -110;

    const ghost = stepLabel.cloneNode(true) as HTMLElement;
    ghost.textContent = outgoingStep;
    ghost.setAttribute("aria-hidden", "true");
    ghost.style.position = "absolute";
    ghost.style.inset = "0";
    ghost.style.margin = "0";
    ghost.style.pointerEvents = "none";
    parent.appendChild(ghost);

    stepLabel.textContent = nextStep;
    stepLabel.style.opacity = "0";
    stepLabel.style.transform = `translate3d(0, ${inFrom}%, 0)`;
    stepLabel.style.filter = "blur(4px)";

    const outAnim = ghost.animate(
      [
        { opacity: 1, transform: "translate3d(0, 0, 0)", filter: "blur(0px)" },
        { opacity: 0, transform: `translate3d(0, ${outTo}%, 0)`, filter: "blur(4px)" }
      ],
      {
        duration: STEP_DURATION,
        easing: EASE,
        fill: "forwards"
      }
    );

    const inAnim = stepLabel.animate(
      [
        { opacity: 0, transform: `translate3d(0, ${inFrom}%, 0)`, filter: "blur(4px)" },
        { opacity: 1, transform: "translate3d(0, 0, 0)", filter: "blur(0px)" }
      ],
      {
        duration: STEP_DURATION,
        easing: EASE,
        fill: "forwards"
      }
    );

    return Promise.allSettled([outAnim.finished, inAnim.finished]).then(() => {
      ghost.remove();
      stepLabel.style.removeProperty("opacity");
      stepLabel.style.removeProperty("transform");
      stepLabel.style.removeProperty("filter");
    });
  };

  const goTo = (nextIndex: number, focus = false): boolean => {
    const target = clamp(nextIndex);
    if (target === activeIndex) return false;
    if (isTransitioning) {
      queuedTabTarget = target;
      queuedTabFocus = queuedTabFocus || focus;
      return false;
    }

    const previousIndex = activeIndex;
    const direction = target > previousIndex ? 1 : -1;
    const jumpDistance = Math.abs(target - previousIndex);

    const outgoingPanel = panels[previousIndex];
    const incomingPanel = panels[target];
    const outgoingGif = gifs[previousIndex];
    const incomingGif = gifs[target];
    const outgoingTab = tabs[previousIndex];
    const incomingTab = tabs[target];

    if (!outgoingPanel || !incomingPanel || !outgoingGif || !incomingGif || !outgoingTab || !incomingTab) {
      return false;
    }

    // Non-adjacent tab jumps are played as chained 1-step animations
    // (e.g. 1 -> 3 -> 5 becomes 1 -> 2 -> 3 -> 4 -> 5).
    if (jumpDistance > 1) {
      queuedTabTarget = target;
      queuedTabFocus = queuedTabFocus || focus;
      return goTo(previousIndex + direction, false);
    }

    if (panelsWrap && !isStaticLayoutMode()) {
      const currentHeight = measurePanelHeight(outgoingPanel);
      const nextHeight = measurePanelHeight(incomingPanel);
      panelsWrap.style.height = `${currentHeight}px`;
      window.requestAnimationFrame(() => {
        panelsWrap.style.height = `${nextHeight}px`;
      });
    }

    activeIndex = target;
    section.dataset.activeIndex = String(activeIndex);
    isTransitioning = true;

    updateA11yAndActiveTab();

    outgoingPanel.setAttribute("aria-hidden", "true");
    incomingPanel.setAttribute("aria-hidden", "false");

    outgoingGif.setAttribute("aria-hidden", "true");
    incomingGif.setAttribute("aria-hidden", "false");

    const nextStep = incomingTab.dataset.step ?? String(activeIndex + 1).padStart(2, "0");

    Promise.allSettled([
      verticalSwap(outgoingPanel, incomingPanel, direction),
      verticalSwap(outgoingGif, incomingGif, direction),
      animateTabs(outgoingTab, incomingTab, direction),
      animateStep(direction, nextStep)
    ]).finally(() => {
      syncPanelsHeight();
      isTransitioning = false;

      if (queuedTabTarget !== null && queuedTabTarget !== activeIndex) {
        const directionToQueued = queuedTabTarget > activeIndex ? 1 : -1;
        const stepTarget = activeIndex + directionToQueued;
        const shouldFocusStep = queuedTabFocus && stepTarget === queuedTabTarget;

        if (shouldFocusStep) queuedTabFocus = false;
        window.requestAnimationFrame(() => {
          goTo(stepTarget, shouldFocusStep);
        });
        return;
      }

      queuedTabTarget = null;
      queuedTabFocus = false;
    });

    if (focus) incomingTab.focus();
    return true;
  };

  tabs.forEach((tab, index) => {
    tab.addEventListener("click", () => {
      goTo(index);
    });

    tab.addEventListener("keydown", (event: KeyboardEvent) => {
      if (event.key === "ArrowRight" || event.key === "ArrowDown") {
        event.preventDefault();
        goTo(activeIndex + 1, true);
      } else if (event.key === "ArrowLeft" || event.key === "ArrowUp") {
        event.preventDefault();
        goTo(activeIndex - 1, true);
      } else if (event.key === "Home") {
        event.preventDefault();
        goTo(0, true);
      } else if (event.key === "End") {
        event.preventDefault();
        goTo(lastIndex, true);
      }
    });
  });

  window.addEventListener(
    "wheel",
    (event: WheelEvent) => {
      if (event.ctrlKey) return;
      const delta = Math.abs(event.deltaY);
      if (delta < 2) return;

      const direction: 1 | -1 = event.deltaY > 0 ? 1 : -1;

      const isSameDirectionAfterRelease =
        !isSectionLocked && lastReleaseDirection !== 0 && direction === lastReleaseDirection;
      if (isSameDirectionAfterRelease) return;

      const isReverseDirectionAfterRelease =
        !isSectionLocked && lastReleaseDirection !== 0 && direction !== lastReleaseDirection;

      const currentY = window.scrollY;
      const shouldLockNow =
        isSectionTopAlignedWithViewport() &&
        (isReverseDirectionAfterRelease || delta <= FAST_SKIP_DELTA_THRESHOLD);

      if (!isSectionLocked && shouldLockNow) {
        event.preventDefault();
        engageSectionLock(getSoftSnapAnchor(currentY, delta));
      }
      if (!isSectionLocked) return;

      event.preventDefault();

      if (wheelLocked || isTransitioning) return;
      if (wheelDirectionAccumulator !== direction) {
        wheelDirectionAccumulator = direction;
        wheelDeltaAccumulator = 0;
      }

      wheelDeltaAccumulator += Math.abs(event.deltaY);
      if (wheelDeltaAccumulator < WHEEL_DELTA_LOCK_THRESHOLD) return;
      wheelDeltaAccumulator = 0;

      const wantsToExitAtBoundary =
        (direction > 0 && activeIndex === lastIndex) || (direction < 0 && activeIndex === 0);

      if (wantsToExitAtBoundary && !isTransitioning) {
        releaseSectionLock(direction);
        return;
      }

      const nextIndex = clamp(activeIndex + direction);
      if (nextIndex === activeIndex) return;

      const moved = goTo(nextIndex);
      if (!moved) return;

      wheelLocked = true;
      window.setTimeout(() => {
        wheelLocked = false;
      }, WHEEL_STEP_COOLDOWN_MS);
    },
    { passive: false, capture: true }
  );

  const sectionObserver = new IntersectionObserver(
    () => {
      const visibilityRatio = getSectionVisibilityRatio();

      if (visibilityRatio < LOCK_RESET_VISIBILITY_RATIO) {
        isSectionLocked = false;
        unfreezePageScroll();
        lastReleaseDirection = 0;
        wheelDeltaAccumulator = 0;
        wheelDirectionAccumulator = 0;
        touchDeltaAccumulator = 0;
        touchDirectionAccumulator = 0;
      }
    },
    {
      threshold: [0, LOCK_RESET_VISIBILITY_RATIO, 0.25, 0.5, 1]
    }
  );

  sectionObserver.observe(hostSection);

  section.addEventListener(
    "touchstart",
    (event: TouchEvent) => {
      if (event.touches.length === 0) return;
      touchStartY = event.touches[0].clientY;
      touchCurrentY = touchStartY;
      touchDeltaAccumulator = 0;
      touchDirectionAccumulator = 0;
    },
    { passive: true }
  );

  section.addEventListener(
    "touchmove",
    (event: TouchEvent) => {
      if (touchStartY === null || event.touches.length === 0) return;

      const currentY = event.touches[0].clientY;
      const previousY = touchCurrentY ?? currentY;
      const stepDelta = Math.abs(previousY - currentY);
      const totalDelta = Math.abs(touchStartY - currentY);
      touchCurrentY = currentY;

      if (totalDelta <= 8) return;

      const direction: 1 | -1 = touchStartY > currentY ? 1 : -1;
      const isSameDirectionAfterRelease =
        !isSectionLocked && lastReleaseDirection !== 0 && direction === lastReleaseDirection;
      if (isSameDirectionAfterRelease) return;

      const shouldLockNow = isSectionTopAlignedWithViewport();

      if (!isSectionLocked && shouldLockNow) {
        event.preventDefault();
        engageSectionLock(getSoftSnapAnchor(window.scrollY, stepDelta));
      }
      if (!isSectionLocked) return;

      event.preventDefault();
      if (isTransitioning || wheelLocked) return;
      if (stepDelta < 2) return;

      if (touchDirectionAccumulator !== direction) {
        touchDirectionAccumulator = direction;
        touchDeltaAccumulator = 0;
      }

      touchDeltaAccumulator += stepDelta;
      if (touchDeltaAccumulator < TOUCH_STEP_LOCK_THRESHOLD) return;
      touchDeltaAccumulator = 0;

      const wantsToExitAtBoundary =
        (direction > 0 && activeIndex === lastIndex) || (direction < 0 && activeIndex === 0);
      if (wantsToExitAtBoundary) {
        releaseSectionLock(direction);
        return;
      }

      const nextIndex = clamp(activeIndex + direction);
      if (nextIndex === activeIndex) return;

      const moved = goTo(nextIndex);
      if (!moved) return;

      wheelLocked = true;
      window.setTimeout(() => {
        wheelLocked = false;
      }, WHEEL_STEP_COOLDOWN_MS);
    },
    { passive: false }
  );

  section.addEventListener("touchend", (event: TouchEvent) => {
    if (touchStartY === null || isTransitioning) return;

    const fallbackTouch =
      event.changedTouches.length > 0 ? event.changedTouches[0].clientY : touchStartY;
    const endY = touchCurrentY ?? fallbackTouch;
    const diffY = touchStartY - endY;

    touchStartY = null;
    touchCurrentY = null;
    touchDeltaAccumulator = 0;
    touchDirectionAccumulator = 0;

    if (isSectionLocked) return;

    // Keep legacy unlocked swipe behavior.
    const swipeThreshold = isMobileLayout() ? 30 : 40;
    if (Math.abs(diffY) < swipeThreshold) return;
    goTo(activeIndex + (diffY > 0 ? 1 : -1));
  });

  window.addEventListener(
    "scroll",
    () => {
      if (isSectionLocked) return;

      const prevY = previousScrollY;
      const currentY = window.scrollY;
      previousScrollY = currentY;

      const direction: 1 | -1 | 0 = currentY > prevY ? 1 : currentY < prevY ? -1 : 0;
      if (direction === 0) return;

      const isSameDirectionAfterRelease = lastReleaseDirection !== 0 && direction === lastReleaseDirection;
      if (isSameDirectionAfterRelease) return;

      const anchor = getSectionAnchorTop();
      const tolerance = getLockTriggerTopTolerance();
      const crossedFromAbove = direction > 0 && prevY < anchor - tolerance && currentY >= anchor - tolerance;
      const crossedFromBelow = direction < 0 && prevY > anchor + tolerance && currentY <= anchor + tolerance;
      const shouldLockNow = isSectionTopAlignedWithViewport() || crossedFromAbove || crossedFromBelow;
      if (!shouldLockNow) return;

      engageSectionLock(anchor);
    },
    { passive: true }
  );

  window.addEventListener(
    "resize",
    () => {
      syncPanelsHeight();
    },
    { passive: true }
  );

  updateA11yAndActiveTab();
  panels.forEach((panel, index) => {
    const isActive = index === activeIndex;
    panel.classList.toggle("is-active", isActive);
    panel.setAttribute("aria-hidden", isActive ? "false" : "true");
    clearInlineFx(panel);
  });

  gifs.forEach((gif, index) => {
    const isActive = index === activeIndex;
    gif.classList.toggle("is-active", isActive);
    gif.setAttribute("aria-hidden", isActive ? "false" : "true");
    clearInlineFx(gif);
  });

  if (stepLabel) {
    const initialStep = tabs[activeIndex]?.dataset.step ?? String(activeIndex + 1).padStart(2, "0");
    stepLabel.textContent = initialStep;
    stepLabel.style.removeProperty("opacity");
    stepLabel.style.removeProperty("transform");
  }

  syncPanelsHeight();
});

