export const LANGUAGES = ["uz", "ru", "en"] as const;

export type Lang = (typeof LANGUAGES)[number];

export const DEFAULT_LANG: Lang = "uz";

export const NON_DEFAULT_LANGUAGES = LANGUAGES.filter(
  (l): l is Exclude<Lang, typeof DEFAULT_LANG> => l !== DEFAULT_LANG
);

import type { Lang } from "./config";

type PageMeta = {
  title: string;
  description: string;
  keywords?: string;
};

type SliderSlide = {
  title: string;
  description: string;
  descriptionAccent?: string;
  source?: string;
  sourceHref?: string;
  imageAlt: string;
  highlight?: string;
  quote?: string;
  sourceInQuote?: boolean;
  hideSource?: boolean;
  value?: string;
  bodyHeight?: number;
  cardHeight?: number;
  sourceWidth?: number;
  descriptionSize?: number;
  leftImageAlt?: string;
  rightImageAlt?: string;
  cardClassName?: string;
};

type SliderSectionContent = {
  title: string;
  titleAccent: string;
  subtitle: string;
  sliderAriaLabel: string;
  dotsAriaLabel: string;
  slideAriaSuffix: string;
  sourceLabel: string;
  accentFirst?: boolean;
  slides: SliderSlide[];
};

type HowDoesItWorkInfoCard = {
  label: string;
  text: string;
  tone: "warning" | "success";
};

type HowDoesItWorkTab = {
  label: string;
  step: string;
  gifAlt: string;
  cards: HowDoesItWorkInfoCard[];
};

