/**
 * Prepends the configured base path to a given path.
 * Safe against double slashes.
 */
export function withBase(path: string): string {
  const basePath = import.meta.env.BASE_URL;
  return `${basePath.replace(/\/+$/, "")}/${path.replace(/^\/+/, "")}`;
}

